import React, { componentDidMount, Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
//import nav from '../Nav.js';

export default class CrearServicio extends Component {

    constructor() {
        super();
        this.state = {
            servicios: [],
            tareas: [],
            nombre: "",
            precio:"",
            garantia:"",
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",

        }
    }
    componentDidMount = () => {
        axios.get('http://localhost:9090/tareas/listar').then((resp) => {
          console.log(resp.data);
          this.setState({
            tareas: resp.data,
          });
        }).catch((error) => {
          console.log(error);
        }
        )
      };
    nombreOnChange = e => {
        this.setState({
            nombre: e.target.value,
            mensaje: "",
        })
    }
    precioOnChange = e => {
        this.setState({
            precio: e.target.value,
        })
    }
    garantiaOnChange = e => {
        this.setState({
            garantia: e.target.value,
        })
    }
    manejadorBoton = () => {
        if (this.state.servicioElegido !== "") {
            axios.post(url + '/servicios/insertar?nombre=' + this.state.nombre + "&precio=" 
            + this.state.precio + "&garantia=" + this.state.garantia)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto", mensajeSuccess: "ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
                    }

                }).catch(error => {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Alta servicio</h1>
                    <form onSubmit={this.submit}>
                        <input type="text" id="crearServicio" class="fadeIn second"
                            name="nombre" placeholder="Nombre del servicio" onChange={this.nombreOnChange} />
                        <input type="text" id="crearServicio" class="fadeIn second"
                            name="precio" placeholder="Precio del servicio" onChange={this.precioOnChange} />
                        <input type="text" id="crearServicio" class="fadeIn second"
                            name="garantia" placeholder="Garantia del servicio" onChange={this.garantiaOnChange} />
                        <br /> <br />
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
                    </form>
                    <div>
          <ReactBoostrap.Table className="table " hover variant="light-dark">
            <thead>
              <th scope="col">ID</th >
              <th scope="col">Nombre</th>
              <th scope="col">Descripcion</th>
            </thead>
            <tbody>
              {
                this.state.tareas.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td>{elemento.descripcion}</td>
                    <td>{elemento.descripcion}</td>
                  </tr>
                ))
              }
            </tbody>
          </ReactBoostrap.Table>
        </div>
                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )

    }
}
