import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';

export default class CrearGarantia extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",
            cantidadMeses: 0,
            errors: {}
        }
       
    }

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    handleValidation(){
        let errors = {};
        let formIsValid = true;

         //Cantidad de meses
         if (this.state.cantidadMeses === 0) {
            formIsValid = false;
            errors["cantidadMeses"] = "Debe ingresar la cantidad de meses";
            document.getElementById("cantidadMeses").focus();
        }

        //Cantidad de meses
        if (this.state.cantidadMeses <= 0) {
            formIsValid = false;
            errors["cantidadMeses"] = "Garantía inválida";
            document.getElementById("cantidadMeses").focus();
        } 
 
       this.setState({errors: errors});
       return formIsValid;
    }

    manejadorBoton = () =>
    {
        if(this.handleValidation()){   
            console.log(this.state.cantidadMeses)        
            axios.post(url + '/garantia/insertar?cantidadMeses='+ this.state.cantidadMeses)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok",cantidadMeses:"",});
                        document.getElementById("cantidadMeses").value = "";
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        }
    }

    render() {
        //const {nombre} = this.state
        return (
            <div>
                <div>
                    <h1>Alta Garantía</h1>
                    <form onSubmit={this.submit}>
                        <span style={{color: "red"}}>{this.state.errors["cantidadMeses"]}</span>
                        <br/>
                        <input type="number" id="cantidadMeses" class="fadeIn second"
                            name="cantidadMeses"  placeholder="Cantidad de la garantía" onChange={this.manejadorOnChange} required/>
                        <br/> <br/>
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }

}
