import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API';

export default class CrearCliente extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            categorias: [],
            nombre: "",
            direccion: "",
            mensajeError: "",
            mensajeSuccess: "",
            catid: "",
            errors: {}
        }
    }

    componentDidMount = () => {
        axios.get(url + '/categoria/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                categorias: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    elegirCategoria = event => {
        console.log(event.target.value)
        this.setState({
            catid: event.target.vale,
        });
    }

    manejadorOnChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    handleValidation() {
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre!!!";
            document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== "") {
            if (!(this.state.nombre).match(/^[a-zA-Z]+$/)) {
                formIsValid = false;
                errors["nombre"] = "El nombre solo acepta letras!!!";
                document.getElementById("nombre").focus();
            }
        }

        //Contrase침a
        if (this.state.direccion === "") {
            formIsValid = false;
            errors["password"] = "Debe ingresar la direcci칩n!!!";
            document.getElementById("direccion").focus();
        }

        this.setState({ errors: errors });
        return formIsValid;
    }


    manejadorBoton = () => {
        if (this.handleValidation()) {
            axios.post(url + '/cliente/insertar' + '?nombre=' + this.state.nombre +
                '&direccion=' + this.state.direccion + '&categoria=' + this.state.catid)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({ 
                            mensaje: "Ingreso Correcto", 
                            mensajeSuccess: "ok",
                            nombre: "",
                            direccion:"",
                         })
                        document.getElementById("nombre").value = "";
                        document.getElementById("direccion").value = "";
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
                    }

                }).catch(error => {
                    console.log(error);
                })
        }
    }

    render() {
        const { nombre, direccion } = this.state
        return (
            <div>
                <div>
                    <h1>Alta Cliente</h1>
                    <select
                        name="categoria"
                        id="select"
                        className="form-control"
                        onChange={this.elegirCategoria}
                    >
                        {this.state.categorias.map(categoriaElegida => (
                            <option
                                key={categoriaElegida}
                                name="categoria"
                                value={categoriaElegida.id}
                                selectedValue={categoriaElegida}
                            >{categoriaElegida.nombre}</option>
                        ))}</select>
                    <br /> <br />
                    <form onSubmit={this.submit}>
                        <span style={{ color: "red" }}>{this.state.errors["nombre"]}</span>
                        <br />
                        <input type="text" class="fadeIn second" name="nombre"
                            placeholder="Nombre del cliente" value={nombre} onChange={this.manejadorOnChange} required />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["direccion"]}</span>
                        <br />
                        <input type="text" class="fadeIn second" name="direccion"
                            placeholder="Direccion del cliente" value={direccion} onChange={this.manejadorOnChange} required />
                        <br />
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
                    </form>
                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )

    }
}
