import React,{Component} from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { connect } from 'react-redux'


export default class ListarServicios extends Component {

  constructor() {
    super();
    this.state = {
        servicios: [],
        log: "",
        servicioElegido: "",
        mensaje: "",
        servicioBorrado: {},
        servicioBorradoId: 0
    }
}
elegirServicio = servicio =>
{
  this.setState({
    servicioElegido: servicio.target.value
  })
}

  componentDidMount = () => {
    axios.get('http://localhost:9090/servicios/listar').then((resp) => {
        console.log(resp.data);
        this.setState({
            servicios: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
};
  render() {
    return (
      <div>
        <h1 >Listar Servicios</h1>
          <ReactBoostrap.Table   className="table " hover variant="light-dark">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th><th scope="col">Precio</th><th scope="col"></th>
              </thead>
                <tbody>
                  {
                    this.state.servicios.map(elemento => (
                      <tr key={elemento.id} value={elemento} id={elemento.id}>
                        <td>{elemento.id}</td>
                        <td>{elemento.nombre}</td>
                        <td>{elemento.precio}</td>
                      </tr>
                    ))
                  }
                </tbody>
          </ReactBoostrap.Table>
        
      </div>
    );

  }
}
