import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from "axios";


export default class ListarServicios extends Component {

  state = {
    data: [],
    servicioBorrado: {},
    servicioBorradoId: 0,
  }
  componentDidMount() {
    this.peticionGet();
  }

  peticionGet = () => {
    axios.get('http://localhost:9090/servicios/listar').then(response => {
      this.setState({ data: response.data });
      console.log(response.data);
    }).catch(error => {
      console.log(error.message);
    })
  }


  elegirServicioBorrado = serBorrado =>
  {
    this.setState({
      servicioBorradoId: serBorrado.target.value
    })
    if (this.state.servicioBorradoId !== 0) {
      this.borrarBoton();    
    }
    console.log(this.state.servicioBorradoId);
  }
  borrarBoton = async () =>
  {
    await axios.post('http://localhost:9090/servicios/borrar?id=' + this.state.servicioBorradoId)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });          
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
  }

  render() {
    return (
      <div>
        <h1>ListarServicios</h1>
        <div>
        <table className="table">
          <thead>
            <tr>
              <th>#</th>
              <th>Nombre</th>
              <th>Accion</th>              
            </tr>
          </thead>
          <tbody>
            {
              this.state.data.map(servicio=>{
                return(
                  <tr>
                    <td>{servicio.id}</td>
                    <td>{servicio.nombre}</td>    
                    <td><ReactBoostrap.Button variant="dark" disable={off} value={servicio.id} onClick={ this.elegirServicioBorrado}>Borrar</ReactBoostrap.Button></td>                   
                  </tr>
                )
              })
            }
          </tbody>
        </table>
        </div>
      </div>
    );
  }
}
