import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 
export default class CrearUsuario extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            usuarioElegido: "",
            nombre :"", 
            password:"",
            mensajeError: "",
            mensajeSuccess:"",
            errors: {}
        }
    }

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
           formIsValid = false;
           errors["nombre"] = "Debe ingresar el nombre!!!";
           document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== ""){
            if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["nombre"] = "El nombre solo acepta letras!!!";
               document.getElementById("nombre").focus();
            }        
        }
 
        //Contraseña
        if (this.state.password === "") {
            formIsValid = false;
            errors["password"] = "Debe ingresar la contraseña!!!";
            document.getElementById("password").focus();
        }
 
        this.setState({errors: errors});
        return formIsValid;
    }


    manejadorBoton = () =>
    {
        if(this.handleValidation()){             
            axios.post(url + '/usuario/insertar/?nombre=' + this.state.nombre + '&password=' + this.state.password)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                        document.getElementById("nombre").value = "";
                        this.state.nombre = "";
                        document.getElementById("password").value = "";
                        this.state.password = "";
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        }
    }

    render() {
        //const {nombre,password} = this.state
        return (
            <div>
                <div>
                    <h1>Alta Usuario</h1>
                    <form onSubmit={this.submit}>
                        <span style={{color: "red"}}>{this.state.errors["nombre"]}</span>
                        <br/>
                        <input type="text" id="nombre" class="fadeIn second"
                            name="nombre"  placeholder="Nombre del usuario" onChange={this.manejadorOnChange} required/>
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["password"]}</span>
                        <br/>
                        <input type="text" id="password" class="fadeIn second"
                            name="password"  placeholder="Contraseña del usuario" onChange={this.manejadorOnChange} required/>
                        <br/><br/>                        
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }
}
