import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 
export default class CrearUsuario extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            empleados:[],
            empleadoElegido: "",
            nombre :"", 
            password:"",
            mensajeError: "",
            mensajeSuccess:"",
            empleadoId: 0,
            email:"",
            idPerfil:'',
        }
    }

    componentDidMount = () => {
        axios.get(url + '/empleado/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                empleados: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }
    manejadorBoton = () =>
    {
        if (this.state.nombre !== "" && this.state.password !=="") {
            axios.post(url + '/usuario/insertar/?nombre=' + this.state.nombre + '&password=' + this.state.password + 
            '&email=' + this.state.email + '&perfil='  + this.state.idPerfil)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
        }
    }

    elergirEmpleado = event => {
        console.log(event.target.value) 
        this.setState({            
            empleadoId: event.target.value,                                       
        });
    }
    
    buscarEmpleado = (empleadoId) => {
        axios.get(url + '/empleado/buscarPorId?id='+ empleadoId).then((resp) => {
            console.log(resp.data);
            this.setState({
                empleadoElegido: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    render() {
        const {nombre,password, email} = this.state
        return (
            <div>
                <div>
                    <h1>Alta Usuario Empleado</h1>
                    <span>Elige el empleado al cual quieras realizarle un usuario.</span>
                    <select             
                        name="empleado"
                        id="select"                        
                        className="form-control"
                        onChange={this.elergirEmpleado, this.buscarEmpleado(value)}                        
                    >
                        {this.state.empleados.map(empleadoElegido => (
                            <option                                
                                key={empleadoElegido}
                                name="empleado"
                                value={empleadoElegido.id}                                                                  
                                selectedValue={empleadoElegido}                       
                            >{empleadoElegido.nombre}</option>
                        ))}</select>
                    <br /> 
                    <form onSubmit={this.submit} >
                        <input type="text" class="fadeIn second" name="nombre" placeholder="Nombre del usuario" value={nombre} onChange={this.manejadorOnChange} />
                        <input type="text" class="fadeIn second" name="password" placeholder="Password del usuario" value={password} onChange={this.manejadorOnChange}  />
                        <input type="text" class="fadeIn second" name="email" placeholder="Email del empleado" value={email} disabled={true}/>
                        <br/> <br/>
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }
}
