import React, { componentDidMount, Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { Link } from 'react-router-dom';


export default class BorrarProveedor extends Component {
  constructor() {
    super();
    this.state = {
      proveedores: [],
      proveedorBorradoId: 0,
      proveedorModificadoId: 0
    }
  }
  componentDidMount = () => {
    axios.get('http://localhost:9090/proveedores/listar').then((resp) => {
      this.setState({
        proveedores: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };
  elegirProveedorBorrado = proveBorrado => {
    this.setState({
      proveedorBorradoId: proveBorrado.target.value
    })
    if (this.state.proveedorBorradoId !== 0) {
      this.borrarBoton();

    }
    console.log(this.state.proveedorBorradoId);
  }
  borrarBoton = () => {
    axios.post('http://localhost:9090/proveedores/borrar?id=' + this.state.proveedorBorradoId)
      .then(response => {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
          this.componentDidMount();
        }

      }).catch(error => {
        console.log(error);
      })
  }

  enviarProveedor = proveedorEnviado => {
    console.log(proveedorEnviado.target.value)
  }

  render() {

    return (
      <div>
        <h1>Borrar Proveedores</h1>
        <ReactBoostrap.Table hover variant="light-dark">
          <table className="table ">
            <thead>
              <th scope="col">ID</th >
              <th scope="col">Nombre</th>
              <th scope="col">Borrar</th>
              <th scope="col">Modificar</th>
            </thead>
            <tbody>
              {
                this.state.proveedores.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.elegirProveedorBorrado}>Borrar</ReactBoostrap.Button></td>
                    <td>
                      <Link className='btn btn-primary' to={'/ModificarProveedor/' + elemento.id} >Modificar</Link>
                    </td>
                  </tr>
                ))
              }
            </tbody>
          </table>
        </ReactBoostrap.Table>


      </div>
    )
  }
}
