import React, { componentDidMount, Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';

export default class ModificarProducto extends Component {
    constructor() {
        super();
        this.state = {
            productos: [],
            ProductoModificado: "",
            ProductoIdMod: 0,
            mensaje: "",               
            mensajeError: "",
            mensajeSuccess: "",
        }
    }
    componentDidMount = () => {
        axios.get(url + '/productos/listar').then((resp) => {
            this.setState({
                productos: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
        console.log(ProductoIdMod);
    };

    elegirProductoModificado = ProductoModificado => {
        this.setState({
            ProductoIdMod: ProductoModificado.target.value
        })
        if (this.state.ProductoIdMod !== 0) {
            this.botonModificar();

        }
        console.log(this.state.ProductoIdMod);
    }

    botonModificar = () => {
        axios.post('http://localhost:9090/productos/modificar?id=' + this.state.ProductoIdMod)
            .then(response => {
                if (response.status === 200) {
                    this.setState({ mensaje: "modificado correctamente" });
                    this.componentDidMount();
                }

            }).catch(error => {
                console.log(error);
            })
    }

    render() {

        return (
            <div>
                <h1>Modificar Producto</h1>
                <form onSubmit={this.submit}>
                    <input type="text" id="modificarProducto" class="fadeIn second" name="nombre" placeholder="Nombre del producto" onChange={this.nombreOnChange} />
                    <input type="text" id="modificarProducto" class="fadeIn second" name="codigoProveedor" placeholder="Codigo del producto en el proveedor" onChange={this.codigoProveedorOnChange} />
                    <input type="text" id="modificarProducto" class="fadeIn second" name="precio" placeholder="Precio del producto" onChange={this.precioOnChange} />
                    <input type="text" id="modificarProducto" class="fadeIn second" name="stock" placeholder="Stock del producto" onChange={this.stockOnChange} />
                    <ReactBoostrap.Button variant="primary" onClick={this.manejadorBoton} >Modificar</ReactBoostrap.Button>
                </form>
            </div>
        )
    }
}
