import React, { componentDidMount, Component } from 'react'
import axios from 'axios'

function primerLlamado() {

    fetch('http://localhost:9090/servicios/insertar')
        .then(function (response) {
            this.setState({
                personas: Object.entries(response.data),
            });
        })
        .then(function (myJson) {
            console.log(myJson);
        }).catch(function (error) {
            console.log("error " + error)
        }
        )
}


export default class CrearServicio extends Component {

    constructor() {
        super();
        this.state = {
            servicios: [],
            log: "",
            mensaje: "",
            servicioBorrado: {},
            servicioBorradoId: 0
        }
    }

    componentDidMount = () => {
        axios.get('http://localhost:9090/servicios/insertar').then((resp) => {
            console.log(resp.data);
            this.setState({
                servicios: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    render() {
        return (
            <div className="container">
                <div className="col">
                    <label id="labeles"
                        className="lead" >Alta de Servicio</label>
                        <br/>
                    <div className="">
                        <select
                            name="servicio"
                            id="select"
                            className="form-control"
                            value={this.state.servicioElegido}
                            onChange={this.elegirServicio}
                            selectedValue={this.state.servicioElegido}
                            placeholderText="Servicios">
                            {/* <option hidden selected id="seleccionarServicio">Servicio</option> */}
                            {this.state.servicios.map(servicioElegido => (
                                <option
                                    // key={servicioElegido}
                                    name="servicio"
                                    value={servicioElegido}
                                    selectedValue={this.state.servicioElegido}
                                >{servicioElegido.nombre}</option>
                            ))}</select>

                    </div>
                    <div>
                        <form onSubmit={this.submit}>
                            <input type="text" id="crearServicio" class="fadeIn second" name="nombre" placeholder="nombre" onChange={this.manejadorOnChange} />
                            <input type="submit" class="fadeIn fourth" value="Crear Servicio" onClick={this.manejadorBoton} />
                        </form>
                    </div>
                </div>
            </div>
        )

    }
}
