import React, { componentDidMount, Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';


export default class CrearServicio extends Component {
    state = {
        data: [],
        modalInsertar: false,
        nombre: '',
    }
    componentDidMount() {
       if(this.state.nombre !== ""){this.peticionPost()}
    }
    peticionPost = async () => {
        await axios.post('http://localhost:9090/servicios/insertar?nombre=' + this.state.nombre).then(response => {
            this.modalInsertar();
            this.peticionGet();
        }).catch(error => {
            console.log(error.message);
        })
    }

    manejadorOnChange = e => {
        this.setState({
            nombre: e.target.value
        })
    }

    submit(e) {
        e.preventDefault();
    }

    //Carga de form
    /*handleChange=async e=>{
        e.persist();
        await this.setState({
          form:{
            ...this.state.form,
            [e.target.name]: e.target.value
          }
        });
        console.log(this.state.form);
      }*/
      

    render() {
        return (
            <div>
                <div>
                    <h1>Alta servicio</h1>
                    <form onSubmit={this.submit}>
                        <input type="text" id="crearServicio" name="nombre" placeholder="Nombre del servicio" onChange={this.manejadorOnChange} />
                        <br /> <br />
                        <ReactBoostrap.Button variant="success"  onClick={ this.peticionPost }>Crear</ReactBoostrap.Button>
                    </form>
                </div>
            </div>
        )

    }
}
