import React, { componentDidMount, Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';

export default class CrearCliente extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",           
            nombre :"", 
            direccion:"",           
            mensajeError: "",
            mensajeSuccess:"",
        }
    }
    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }
    manejadorBoton = () =>
    {
        if (this.state.nombre !== "" && this.state.direccion !== "") {            
            axios.post('http://localhost:9090/clientes/insertar?nombre='+ this.state.nombre + 
            '&direccion='+ this.state.direccion)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
        }
    }

    render() {
        const {nombre,direccion} = this.state
        return (
            <div>
                <div>
                    <h1>Alta Cliente</h1>
                    <form onSubmit={this.submit}>
                        <input type="text"  class="fadeIn second" name="nombre" placeholder="Nombre del cliente" value={nombre} onChange={this.manejadorOnChange} required/>
                        <input type="text"  class="fadeIn second" name="direccion" placeholder="Direccion del cliente" value={direccion} onChange={this.manejadorOnChange} required/>                       
                        <br/>
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }
}
