import React, { Component } from 'react'

import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 

export default class BorrarUsuario extends Component
{
  constructor()
  {
    super();
    this.state = {
      usuarios: [],
      usuarioBorradoId: 0,
    }
  }
  componentDidMount = () => {
    axios.get(url + '/usuarios/listar').then((resp) => {
        this.setState({
            usuarios: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };
  elegirUsuarioBorrado = userBorrado =>
  {
    this.setState({
      usuarioBorradoId: userBorrado.target.value
    })
    if (this.state.usuarioBorradoId !== 0) {
      this.borrarBoton();
      
    }
    console.log(this.state.usuarioBorradoId);
  }
  borrarBoton = () =>
  {
    axios.post('http://localhost:9090/usuarios/borrar?id=' + this.state.usuarioBorradoId)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
          this.componentDidMount();
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
  }
  
  render()
  {

    return (
      <div>
       
          <h1>Borrar Usuarios</h1>
         

            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th>
                <th scope="col"></th>
              </thead>
              <tbody>
              {
                this.state.usuarios.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.elegirUsuarioBorrado}>borrar</ReactBoostrap.Button></td>
                  </tr>
                ))
                }
                  </tbody>
              </table>
          </ReactBoostrap.Table>
          
        
      </div>
    )
  }
}
