import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';


export default class ModificarProducto extends Component {
    constructor(props) {
        super(props);
        this.state = {
            empleado: '', 
            ProductoModificado: "",
            ProductoIdMod: 0,  
            nombre:'',         
            mensaje: "",
            telefono:"",
            direccion:"",
            mail:"",
            fechaIngreso:"",
            mensajeError: "",            
            mensajeSuccess: "", 
            errors: {}                       
        }
    }
    componentDidMount = () => {
        console.log(this.props.match.params.id)
        axios.get(url + '/empleado/buscarPorId?id=' + this.props.match.params.id).then((resp) => {        
            console.log(resp.data)            
            this.setState({
                empleado: resp.data, 
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
           formIsValid = false;
           errors["nombre"] = "Debe ingresar el nombre!!!";
           //document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== ""){
            if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["nombre"] = "El nombre solo acepta letras!!!";
               //document.getElementById("nombre").focus();
            }        
        }

         //Dirección
         if (this.state.direccion === "") {
            formIsValid = false;
            errors["direccion"] = "Debe ingresar la direccion!!!";
            //document.getElementById("direccion").focus();
         }

         //Teléfono
        if (this.state.telefono === "") {
            formIsValid = false;
            errors["telefono"] = "Debe ingresar el teléfono!!!";
            //document.getElementById("telefono").focus();
        }
 
         //Fecha ingreso
         if (this.state.fechaIngreso === "") {
            formIsValid = false;
            errors["fechaIngreso"] = "Debe ingresar la fecha de ingreso!!!";
            //document.getElementById("fechaIngreso").focus();
        }

        //Mail
        if(this.state.mail === ""){
            formIsValid = false;
            errors["mail"] = "Debe ingresar el mail!!!";
            //document.getElementById("mail").focus();
        }
 
        if(typeof this.state.mail !== "undefined"){
            let lastAtPos = this.state.mail.lastIndexOf('@');
            let lastDotPos = this.state.mail.lastIndexOf('.');
 
            if (!(lastAtPos < lastDotPos && lastAtPos > 0 && this.state.mail.indexOf('@@') == -1 && lastDotPos > 2 && (this.state.mail.length - lastDotPos) > 2)) {
               formIsValid = false;
               errors["mail"] = "El mail no es válido!!!";
               //document.getElementById("mail").focus();
            }
        }  

        this.setState({errors: errors});
        return formIsValid;
    }


    botonModificar = () =>
    {     
        if(this.handleValidation()){         
            axios.post(url + '/empleado/modificar/?id=' + this.props.match.params.id + '&nombre=' + this.state.nombre +
            '&fechaIngreso='+ this.state.fechaIngreso +'&direccion=' + this.state.direccion +'&telefono=' + this.state.telefono +'&email=' + this.state.email)
            .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }
    
                }).catch(error =>
                {
                    console.log(error);
                })
        }
    }


    render() {
        const {empleado, nombre, fechaIngreso, direccion, telefono, email} = this.state
        return (
            <div>
                <h1>Modificar Empleado</h1>
                <form onSubmit={this.submit}>
                    <span style={{color: "red"}}>{this.state.errors["nombre"]}</span>
                    <br/>
                    <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + empleado.nombre} onChange={this.manejadorOnChange} />
                    <br/>
                    <span style={{color: "red"}}>{this.state.errors["fechaIngreso"]}</span>
                    <br/>
                    <input type="date" name='fechaIngreso' value={fechaIngreso} placeholder={"Fecha de Ingreso: " + empleado.fechaIngreso} onChange={this.manejadorOnChange} />
                    <br/>
                    <span style={{color: "red"}}>{this.state.errors["direccion"]}</span>
                    <br/>
                    <input type="text" name='direccion' value={direccion} placeholder={"Direccion: " + empleado.direccion} onChange={this.manejadorOnChange} />
                    <br/>
                    <span style={{color: "red"}}>{this.state.errors["telefono"]}</span>
                    <br/>
                    <input type="number" name='telefono' value={telefono} placeholder={"Telefono: " + empleado.telefono} onChange={this.manejadorOnChange} />
                    <br/>
                    <span style={{color: "red"}}>{this.state.errors["mail"]}</span>
                    <br/>
                    <input type="text" name='email' value={email} placeholder={"Email: " + empleado.email} onChange={this.manejadorOnChange} />                    
                    <br/><br/>
                    <ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
                </form>
                {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
            </div>
        )
    }
}
