import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { url } from '../API';

export default class ListarClientes extends Component {

    constructor() {
        super();
        this.state = {
            clientes: [],
            mensaje: '',
        }
    }

    componentDidMount = () => {
        axios.get(url + '/cliente/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                clientes: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };


    render() {
    return (
      <div>
        <h1 >Listar Clientes</h1>
        <div>
          <ReactBoostrap.Table hover variant="light-dark">
            <table className="table ">
              <thead>
                <td scope="col">#</td >
                <th scope="col">Nombre</th>
                <th scope="col">Direccion</th>
                <th scope="col">Categoria</th>     
                <th scope="col">Descuento</th>           
              </thead>
              <tbody>
                {
                  this.state.clientes.map(elemento => (
                    <tr key={elemento.id} value={elemento} id={elemento.id}>
                      <td>{elemento.id}</td>
                      <td>{elemento.nombre}</td>
                      <td>{elemento.direccion}</td>
                      <td>{elemento.categoria.nombre}</td>     
                      <td>{elemento.categoria.descuento}</td>                 
                    </tr>
                  ))
                }
              </tbody>
            </table>
          </ReactBoostrap.Table>
        </div>
      </div>
    );

  }
}
