import React, { Component } from "react";
import { createStore } from 'redux'
import './App.css';
import Nav from './components/Nav.js';
import { Provider } from 'react-redux'
import "bootstrap/dist/css/bootstrap.min.css"

import Login from './components/Login.js';

import ListarServicios from './components/Servicio/ListarServicios.js';
import CrearServicio from './components/Servicio/CrearServicio.js';
import BorrarServicio from './components/Servicio/BorrarServicio.js';
import ModificarServicio from './components/Servicio/ModificarServicio.js';

import CrearProducto from './components/Producto/CrearProducto.js';
import ListarProductos from './components/Producto/ListarProductos.js';
import BorrarProducto from './components/Producto/BorrarProducto.js';
import ModificarProducto from './components/Producto/ModificarProducto.js';

import CrearUsuario from './components/Usuario/CrearUsuario.js';
import ListarUsuarios from './components/Usuario/ListarUsuarios.js';
import BorrarUsuario from './components/Usuario/BorrarUsuario.js';

import CrearProveedor from './components/Proveedor/CrearProveedor.js';
import ListarProveedores from './components/Proveedor/ListarProveedores.js';
import BorrarProveedor from './components/Proveedor/BorrarProveedor.js';
import ProductosProveedor from './components/Proveedor/ProductosProveedor.js';
import ModificarProveedor from './components/Proveedor/ModificarProveedor.js';

import CrearEmpleado from './components/Empleado/CrearEmpleado.js';
import ListarEmpleados from './components/Empleado/ListarEmpleados.js';
import BorrarEmpleado from './components/Empleado/BorrarEmpleado.js';
import ModificarEmpleado from './components/Empleado/ModificarEmpleado.js';

import AgendarServicio from './components/Agenda/AgendarServicio.js';
import ListarAgenda from './components/Agenda/ListarAgenda.js';
import BorrarAgendaServicio from './components/Agenda/BorrarAgendaServicio.js';

import CrearTarea from './components/Tarea/CrearTarea.js';
import BorrarTarea from './components/Tarea/BorrarTarea.js';
import ListarTareas from './components/Tarea/ListarTareas.js';

import ListarNotificacionesUsuario from './components/Notificacion/ListarNotificacionesUsuario.js';

import CrearCategoria from './components/Categoria/CrearCategoria.js';
import ListarCategoria from './components/Categoria/ListarCategoria.js';
import BorrarCategoria from './components/Categoria/BorrarCategoria.js';
import ModificarCategoria from './components/Categoria/ModificarCategoria.js';

import CrearPerfil from './components/Perfil/CrearPerfil.js';
import ListarPerfil from './components/Perfil/ListarPerfil.js';
import BorrarPerfil from './components/Perfil/BorrarPerfil.js';
import ModificarPerfil from "./components/Perfil/ModificarPerfil";

import CrearCliente from './components/Cliente/CrearCliente.js';
import ListarClientes from './components/Cliente/ListarClientes.js';
import BorrarCliente from './components/Cliente/BorrarCliente.js';
import ModificarCliente from './components/Cliente/ModificarCliente.js';

import CrearEstado from './components/Estado/CrearEstado.js';
import ListarEstado from './components/Estado/ListarEstado.js';
import ModificarEstado from './components/Estado/ModificarEstado.js';
import BorrarEstado from './components/Estado/BorrarEstado.js';

import PantallaPrincipal from './PantallaPrincipal/PantallaPrincipal.js';


import { BrowserRouter as Router, Route } from 'react-router-dom'
import ModificarUsuario from "./components/Usuario/ModificarUsuario";
import RegistroUsuario from "./components/Usuario/RegistroUsuario";
import Logout from "./components/Logout";



const initialState = {
  personas: [],
  log: false,
  listarSer: false,
  crearSer: false,
  borrarSer: false,
};

function reducer(state = initialState, action) {
  switch (action.type) {
    case "INICIO":
      return { ...state, personas: action.payload };
    // case "PANTALLA-INICIO":
    //   return { ...state, log: action.payload };
    // case "LISTAR-SERVICIOS":
    //   return { ...state, listarSer: action.payload };
    // case "CREAR-SERVICIO":
    //   return { ...state, crearSer: action.payload };
    //   case "BORRAR-SERVICIO":
    //     return { ...state, borrarSer: action.payload };
    default:
      return state;
  }
}


const store = createStore(reducer);
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      personas: [],
      nombrePerfil:'',
      form: {
        user: '', password: '',
      }
    }
  }


  render() {
    return (
      <Router>

        <div className="App">
        <Nav/>

          <Provider store={store}>         

            <Route path="/Login" component={Login}></Route>
            <Route path="/logout" component={Logout}></Route>
            <Route path="/RegistroUsuario" component={RegistroUsuario}></Route>
            {/* SERVICIOS */}
            <Route path="/CrearServicio" component={CrearServicio}></Route>
            <Route path="/ListarServicios" component={ListarServicios}></Route>
            <Route path="/BorrarServicio" component={BorrarServicio}></Route>
            <Route path="/ModificarServicio/:id" component={ModificarServicio}></Route>
            {/* PRODUCTOS */}
            <Route path="/CrearProducto" component={CrearProducto}></Route>
            <Route path="/ListarProductos" component={ListarProductos}></Route>
            <Route path="/BorrarProducto" component={BorrarProducto}></Route>
            <Route path="/ModificarProducto/:id" component={ModificarProducto}></Route>
            {/* USUARIOS */}
            <Route path="/CrearUsuario" component={CrearUsuario}></Route>
            <Route path="/ListarUsuarios" component={ListarUsuarios}></Route>
            <Route path="/BorrarUsuario" component={BorrarUsuario}></Route>
            <Route path="/ModificarUsuario/:id" component={ModificarUsuario}></Route>
            {/* PROVEEDOR */}
            <Route path="/CrearProveedor" component={CrearProveedor}></Route>
            <Route path="/ListarProveedores" component={ListarProveedores}></Route>
            <Route path="/BorrarProveedor" component={BorrarProveedor}></Route>
            <Route path="/ProductosProveedor" component={ProductosProveedor}></Route>
            <Route path="/ModificarProveedor/:id" component={ModificarProveedor}></Route>
            {/* EMPLEADOS */}
            <Route path="/CrearEmpleado" component={CrearEmpleado}></Route>
            <Route path="/ListarEmpleados" component={ListarEmpleados}></Route>
            <Route path="/BorrarEmpleado" component={BorrarEmpleado}></Route>
            <Route path="/ModificarEmpleado/:id" component={ModificarEmpleado}></Route>
            {/* AGENDA */}
            <Route path="/AgendarServicio" component={AgendarServicio}></Route>
            <Route path="/ListarAgenda" component={ListarAgenda}></Route>
            <Route path="/BorrarAgendaServicio" component={BorrarAgendaServicio}></Route>
            {/* TAREA */}
            <Route path="/CrearTarea" component={CrearTarea}></Route>
            <Route path="/BorrarTarea" component={BorrarTarea}></Route>
            <Route path="/ListarTareas" component={ListarTareas}></Route>
            {/* ESTADO */}
            <Route path="/CrearEstado" component={CrearEstado}></Route>
            <Route path="/ListarEstado" component={ListarEstado}></Route>
            <Route path="/BorrarEstado" component={BorrarEstado}></Route>
            <Route path="/ModificarEstado/:id" component={ModificarEstado}></Route>           
            {/* NOTIFICACIONES */}
            <Route path="/ListarNotificacionesUsuario" component={ListarNotificacionesUsuario}></Route>

            {/* CATEGORIA */}
            <Route path="/CrearCategoria" component={CrearCategoria}></Route>
