            <Route path="/BorrarCategoria" component={BorrarCategoria}></Route>
            <Route path="/ModificarCategoria/:id" component={ModificarCategoria}></Route>

            {/* CLIENTE */}
            <Route path="/CrearCliente" component={CrearCliente}></Route>
            <Route path="/ListarCliente" component={ListarClientes}></Route>
            <Route path="/BorrarCliente" component={BorrarCliente}></Route>
            <Route path="/ModificarCliente/:id" component={ModificarCliente}></Route>

            {/* PERFIL */}
            <Route path="/CrearPerfil" component={CrearPerfil}></Route>
