            <Route path="/ModificarPerfil/:id" component={ModificarPerfil}></Route>
            {/* PANTALLA PRINCIPAL */}
            <Route path="/PantallaPrincipal"  path = "/*"render={props => (<PantallaPrincipal {...props} />)}></Route>



          </Provider>

        </div>
      </Router>
    );
  }
}
export default (App);
