import React, { componentDidMount, Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
import BorrarProducto from './BorrarProducto'

export default class ModificarProducto extends Component {
    constructor(props) {
        super(props);
        this.state = {
            producto: '',
            ProductoModificado: "",
            ProductoIdMod: 0,
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",            
        }
    }
    componentDidMount = () => {
        console.log(this.props.match.params.id)
        axios.get(url + '/productos/buscarPorId?id=' + this.props.match.params.id).then((resp) => {
            console.log(resp.data)            
            this.setState({
                producto: resp.data,                                    
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    enviarProducto = productoEnviado => {
        console.log(productoEnviado.target.value)
    }

    render() {
        const {producto} = this.state
        return (
            <div>
                <h1>Modificar Producto</h1>
                <form onSubmit={this.submit}>
                    <input type="text" id="modificarProducto" class="fadeIn second" name="nombre" placeholder={"Nombre: " + producto.nombre} onChange={this.nombreOnChange} />
                    <input type="text" id="modificarProducto" class="fadeIn second" name="codigoProveedor" placeholder={"Cod Proveedor: " + producto.codigoProveedor} onChange={this.codigoProveedorOnChange} />
                    <input type="text" id="modificarProducto" class="fadeIn second" name="precio" placeholder={"Precio: " + producto.precio} onChange={this.precioOnChange} />
                    <input type="text" id="modificarProducto" class="fadeIn second" name="stock" placeholder={"Stock: " + producto.stock} onChange={this.stockOnChange} />
                    <br /><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
                </form>
            </div>
        )
    }
}
