import React, { componentDidMount, Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';

export default class CrearTarea extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            nombre :"",
            direccion:"",
            tareaElegido: "",
            mensajeError: "",
            mensajeSuccess:"",
        }
    }
    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }
    manejadorBoton = () =>
    {
        if (this.state.usuarioElegido !== "") {
            axios.post('http://localhost:9090/tareas/insertar?nombre=' + this.state.tareaElegido)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Alta Tarea</h1>
                    <form onSubmit={this.submit}>
                        <input type="text" id="crearTarea" class="fadeIn second" name="nombre" placeholder="Nombre de la Tarea" onChange={this.manejadorOnChange} required/>
                        <input type="text" id="crearTarea" class="fadeIn second" name="descripcion" placeholder="Descripcion de la Tarea" onChange={this.manejadorOnChange} required/>
                        <br/> <br/> 
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }
}
