import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from "axios";


export default class ListarServicios extends Component {

  state = {
    data: []
  }
  componentDidMount() {
    this.peticionGet();
  }

  peticionGet = () => {
    axios.get('http://localhost:9090/servicios/listar').then(response => {
      this.setState({ data: response.data });
      console.log(response.data);
    }).catch(error => {
      console.log(error.message);
    })
  }


  peticionDelete=()=>{
    axios.post('http://localhost:9090/servicios/borrar?id=' + this.state.form.id ).then(response=>{
      this.setState({modalEliminar: false});
      this.peticionGet();
    }).catch(error=>{
      console.log(error.message);
    })
  }

  render() {
    return (
      <div>
        <h1>ListarServicios</h1>
        <div>
        <table className="table">
          <thead>
            <tr>
              <th>#</th>
              <th>Nombre</th>
              <th>Accion</th>              
            </tr>
          </thead>
          <tbody>
            {
              this.state.data.map(servicio=>{
                return(
                  <tr>
                    <td>{servicio.id}</td>
                    <td>{servicio.nombre}</td>    
                    <td><ReactBoostrap.Button variant="secundary" onClick={ this.peticionDelete }>Borrar</ReactBoostrap.Button></td>                   
                  </tr>
                )
              })
            }
          </tbody>
        </table>
        </div>
      </div>
    );
  }
}
