import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import{ url} from '../API.js'
export default class BorrarTarea extends Component
{
  constructor()
  {
    super();
    this.state = {
      mensaje:'',
      tareas: [],
      tareaBorradoId: 0,
    }
  }
  componentDidMount = () => {
    axios.get(url + '/tarea/listarActivos').then((resp) => {
        this.setState({
            tareas: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };
 
  borrarBoton = (id) =>
  {
    this.setState({
      tareaBorradoId: id
    })
    axios.post(url + '/tarea/borrar?id=' +id)
      .then(response =>
      {
        if (response.status >= 200 && response.status <= 205) {      
          this.setState({
             mensaje: response.data,
             mensajeSuccess:"ok"
            });          
            this.limpiar(); 
        }
      }).catch(error => {
        this.setState({
          mensaje: error.response.data,
          mensajeError: "error"
        });
        this.limpiar();        
      })
  }

  limpiar() {
    this.timer = setInterval(() => {
        window.location.reload(1);
    }
        , 900);
}
  
  render()
  {

    return (
      <div>
       
          <h1>Borrar Tareas</h1>
         

            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th>
                <th scope="col"></th>
              </thead>
              <tbody>
              {
                this.state.tareas.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={() => this.borrarBoton(elemento.id)}>Borrar</ReactBoostrap.Button></td>
                  </tr>
                ))
                }
                  </tbody>
              </table>
          </ReactBoostrap.Table>
          {this.state.mensajeSuccess === "ok" ?
              <div class="alert alert-success" role="alert">
                {this.state.mensaje}
              </div> : (this.state.mensajeError === "error") ?
                <div class="alert alert-danger" role="alert">
                  {this.state.mensaje}
                </div> : null
            }        
      </div>
    )
  }
}
