import React, { Component } from 'react'

import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 
import { Link } from 'react-router-dom';
export default class BorrarServicio extends Component
{
  constructor()
  {
    super();
    this.state = {
      servicios: [],
      servicioBorrado: {},
      servicioBorradoId: 0,
    }
  }
  componentDidMount = () => {
    axios.get(url + '/servicio/listarActivos').then((resp) => {
        this.setState({
            servicios: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };

  borrarBoton = (id) =>
  {
    this.setState({
      servicioBorradoId: id
    })
    axios.post(url +  '/servicio/borrar?id=' + id)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
         
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
      window.location.reload();
  }
  
  render()
  {

    return (
      <div>
       
          <h1>Acciones Servicios</h1>
         

            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th>
                <th scope="col">Precio</th>
                <th scope="col">Borrar</th>
              <th scope="col">Modificar</th>
              </thead>
              <tbody>
              {
                this.state.servicios.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td>{elemento.precio}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={() => this.borrarBoton(elemento.id)}>Borrar</ReactBoostrap.Button></td>
                       <td>
                      <Link className='btn btn-primary' to={'/ModificarServicio/' + elemento.id} >Modificar</Link>
                    </td>
                  </tr>
                ))
                }
                  </tbody>
              </table>
          </ReactBoostrap.Table>
          
        
      </div>
    )
  }
}
