import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { connect } from 'react-redux'

export default class ListarTareas extends Component {

  constructor() {
    super();
    this.state = {
      tareas: [],
      mensaje: "",
    }
  }
  componentDidMount = () => {
    axios.get('http://localhost:9090').then((resp) => {
      console.log(resp.data);
      this.setState({
        tareas: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };
  render() {
    return (
      <div>
        <h1 >Listar Tareas</h1>
        <div>
          <ReactBoostrap.Table className="table " hover variant="light-dark">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th>
              <th scope="col"></th>
            </thead>

            <tbody>
              {
                this.state.tareas.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                  </tr>
                ))
              }
            </tbody>
          </ReactBoostrap.Table>
        </div>
      </div>
    );

  }
}
