import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
export default class CrearProductos extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            productoElegido: "",
            mensajeError: "",
            mensajeSuccess: "",
            proveedores: [],
                nombre: "",
                codigoProveedor: 0,
                precio: 0,
                stock: 0,
            proveedorId: 0,
        }
       
    }
    nombreOnChange = e => {
        this.setState({
                nombre: e.target.value, 
        })
    }
    codigoProveedorOnChange = e => {
        this.setState({
                codigoProveedor: e.target.value,
        })
    }
    precioOnChange = e => {
        this.setState({
                precio: e.target.value,
        })
    }
    stockOnChange = e => {
        this.setState({
                stock: e.target.value
        })
    }
    elegirProveedor = event => {
        console.log(event.target.value)
        this.setState({
            proveedorId: event.target.value,
        });
    }
    componentDidMount = () => {
        axios.get(url + '/proveedor/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                proveedores: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };
    manejadorBoton = () => {
        axios.post(url + '/producto/insertar?nombre=' + this.state.nombre + "&codigoProveedor="
            + this.state.codigoProveedor + "&precio=" + this.state.precio +
            "&stock=" + this.state.stock + "&proveedor=" + this.state.proveedorId)
            .then(response => {
                if (response.status === 200) {
                    this.setState({ mensaje: "ingreso correcto", mensajeSuccess: "ok" });
                } else {
                    this.setState({ mensaje: "ingreso incorrecto", mensajeError: "error" });
                }
            }).catch(error => {
                console.log(error);
            })

    }

    render() {
        return (
            <div>
                <div>
                    <h1>Alta Producto</h1>
                    <select
                        name="proveedor"
                        id="select"
                        className="form-control"
                        onChange={this.elegirProveedor}
                    >
                        {this.state.proveedores.map(proveedorElegido => (
                            <option
                                key={proveedorElegido}
                                name="proveedor"
                                value={proveedorElegido.id}
                                selectedValue={proveedorElegido}
                            >{proveedorElegido.nombre}</option>
                        ))}</select>
                    <br /> <br />
                    <form onSubmit={this.submit}>
                        <input type="text" id="crearProducto" class="fadeIn second" name="nombre" placeholder="Nombre del producto" onChange={this.nombreOnChange} />
                        <input type="text" id="crearProducto" class="fadeIn second" name="codigoProveedor" placeholder="Codigo del producto en el proveedor" onChange={this.codigoProveedorOnChange} />
                        <input type="text" id="crearProducto" class="fadeIn second" name="precio" placeholder="Precio del producto" onChange={this.precioOnChange} />
                        <input type="text" id="crearProducto" class="fadeIn second" name="stock" placeholder="Stock del producto" onChange={this.stockOnChange} />
                        <br/><ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
                    </form>


                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )

    }
}
