import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';

export default class RegistroUsuario extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            empleados: [],
            usuarioElegido: "",
            nombre: "",
            password: "",
            mensajeError: "",
            mensajeSuccess: "",
            empleadoId: 0,
            email: "",
            cedula: '',
            direccion: '',
            errors: {},
        }
    }
    manejadorOnChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }
    manejadorBoton = () => {
        // this.limpiar();
        if (this.handleValidation()) {
            axios.post(url + '/usuario/insertar/?nombre=' + this.state.nombre +
                '&password=' + this.state.password + '&email=' + this.state.email +
                '&idPerfil=1&cedula=' + this.state.cedula + '&direccion=' + this.state.direccion)
                .then(response => {
                    if (response.status >= 200 && response.status <= 205) {
                        this.setState({
                            mensaje: "Ingreso Correcto",
                            mensajeSuccess: "ok",
                            nombre: "",
                            password: "",
                            email: "",

                        });
                    }

                }).catch((error) => {
                    this.setState({
                        mensaje: error.response.data.message,
                        mensajeError: "error"
                    });
                })
        }
    }

    formatoCedulaOK(cedula) {
        if (cedula.length > 6&&cedula.length <= 7) {
            let verifieDigit = cedula.substring(6,[7]);
            let a = 0;
            let i = 0;
            for (i = 0; i < 7; i++) {
                a += (parseInt("2987634"[i]) * parseInt(cedula[i])) % 10;
            }
            if(a%10===0){
                a= 0;
            }else{
                return verifieDigit === (10 - a) % 10;
            }
        }
        return false;
    }
    handleValidation() {

        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre";
            //document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== "") {
            if (!(this.state.nombre).match(/^[a-zA-Z]+$/)) {
                formIsValid = false;
                errors["nombre"] = "El nombre solo acepta letras";
                //document.getElementById("nombre").focus();
            }
        }

        //Password
        if (this.state.password === "") {
            formIsValid = false;
            errors["password"] = "Debe ingresar la contraseña";
            //document.getElementById("password").focus();
        }
        const regex = /^[0-9]*$/;
        const soloNumeros = regex.test(this.state.cedula);
        //Cedula
        if (this.formatoCedulaOK(this.state.cedula)) {
            formIsValid = false;
            errors["cedula"] = "Formato de la cédula incorrecto";
        }
        else {
            if (this.state.cedula === "") {
                formIsValid = false;
                errors["cedula"] = "Debe ingresar la cédula";
                //document.getElementById("password").focus();
            } else {
                if (!soloNumeros) {
                    formIsValid = false;
                    errors["cedula"] = "Debe ingresar solo números";
                }
            }
        }

        //Direccion
        if (this.state.direccion === "") {
            formIsValid = false;
            errors["direccion"] = "Debe ingresar la dirección";
            //document.getElementById("password").focus();
        }

        //Mail
        if (this.state.email === "") {
            formIsValid = false;
            errors["email"] = "Debe ingresar el email";
            //document.getElementById("email").focus();
        }

        if (this.state.email !== "") {
            let lastAtPos = this.state.email.lastIndexOf('@');
            let lastDotPos = this.state.email.lastIndexOf('.');

            if (!(lastAtPos < lastDotPos && lastAtPos > 0
                && this.state.email.indexOf('@@') === -1 && lastDotPos > 2
                && (this.state.email.length - lastDotPos) > 2)) {
                formIsValid = false;
                errors["email"] = "El email no es válido";
                //document.getElementById("email").focus();
            }
        }


        this.setState({ errors: errors });
        return formIsValid;
    }
    componentWillUnmount() {

        clearInterval(this.timer);
    }
    limpiar() {
        this.timer = setInterval(() => {
            this.setState({
                mensajeSuccess: "",
                mensajeError: "",
                errors: "",
                nombre: "",
                password: "",
                email: "",
                cedula: "",
                direccion: "",
            });
        }
            , 5000);
    }
    render() {
        const { nombre, password, email, cedula, direccion } = this.state
        return (
            <div>
                <div>
                    <h1>Registrate</h1>
                    <br />
                    <form onSubmit={this.submit}>
                        <span style={{ color: "red" }}>{this.state.errors["nombre"]}</span>
                        <br />
                        <input type="text" className="fadeIn second" name="nombre" placeholder="Nombre del usuario" value={nombre} onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["password"]}</span>
                        <br />
                        <input type="password" className="fadeIn second" name="password" placeholder="Password del usuario" value={password} onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["email"]}</span>
                        <br />
                        <input type="text" className="fadeIn second" name="email" placeholder="Email del usuario" value={email} onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["cedula"]}</span>
                        <br />
                        <input type="text" class="fadeIn second" name="cedula" id="cedula" value={cedula}
                            placeholder="C.I. del usuario" onChange={this.manejadorOnChange} required />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["direccion"]}</span>
                        <br />
                        <input type="text" class="fadeIn second" name="direccion" id="direccion" value={direccion}
                            placeholder="Direccion del usuario" onChange={this.manejadorOnChange} required />
                        <br /><br />
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
                    </form>
                    {this.state.mensajeSuccess === "ok" ?
                        <div className="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div className="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )

    }
}
