import React, { componentDidMount, Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';

export default class CrearProductos extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            productoElegido: "",
            mensajeError: "",
            mensajeSuccess: "",
            proveedores: {},

        }
    }
    manejadorOnChange = e =>
    {
        this.setState({
            productoElegido: e.target.value,
            mensaje:"",
        })
    }
    componentDidMount = () => {
        axios.get('http://localhost:9090/productos/listar').then((resp) => {
            console.log(resp.data);
            this.setState({
                proveedores: resp.data,              
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };
    manejadorBoton = () =>
    {
        if (this.state.productoElegido !== "") {
            axios.post('http://localhost:9090/productos/insertar?nombre=' + this.state.productoElegido)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "ingreso correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "ingreso incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "ingreso incorrecto",mensajeError:"error" });
        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Alta Producto</h1>
                    <form onSubmit={this.submit}>
                        <input type="text" id="crearProducto" class="fadeIn second" name="nombre" placeholder="Nombre del producto" onChange={this.manejadorOnChange} />
                        <input type="text" id="crearProducto" class="fadeIn second" name="precio" placeholder="Precio del producto" onChange={this.manejadorOnChange} />
                        <input type="text" id="crearProducto" class="fadeIn second" name="stock" placeholder="Stock del producto" onChange={this.manejadorOnChange} />
                        {/* <select
                            name="proveedor"
                            id="select"
                            className="form-control"
                           
                            onChange={this.elegirProveedor}
                            selectedValue={this.state.proveedorElegido}
                            placeholderText="Proveedores">
                            {}
                            {this.state.proveedores.map(proveedorElegido => (
                                <option
                                     key={proveedorElegido}
                                    name="proveedor"
                                    value={proveedorElegido}
                                    selectedValue={proveedorElegido}
                                >{proveedorElegido.nombre}</option>
                            ))}</select> */}
                        <br /> <br />
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }
}
