import React, { Component } from "react";
import './Tarjeta.css';
import  TarjetaItem  from './TarjetaItem.js'
import axios from 'axios'


export default class Tarjeta extends Component {

    constructor() {
        super();
        this.state = {
            servicios: [],
        }
    }

    peticionListarServiciosActivos=()=>{
        axios.get('http://localhost:9090' +'/servicio/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                servicios: resp.data,              
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    }
 
    componentDidMount = () => {
        this.peticionListarServiciosActivos();
    };

    render() {
        return (
            <div className='cards'>
                <h1>Nuestros Servicios</h1>
                <div className='cards__container'>
                    <div className='cards__wrapper'>
                        <ul className='cards__items'>
                            {this.state.servicios.map(elemento => (
                                    <TarjetaItem
                                        src = {'images/' + elemento.imagen}
                                        title = {elemento.nombre}
                                        text = {elemento.descripcion}
                                        label = 'Contactanos'
                                        path='ContactanosServicio/:' 
                                    />
                            ))}             
                            <TarjetaItem
                                label='Agenda Online'
                                src='images/aceite.jpg'
                                title="Cambio de aceite y filtros"
                                text='El aceite lubrica y permite el movimiento de las piezas internas del motor. 
                                Renovarlo regularmente, es muy importante para preservar la vida útil del motor.'
                                path='/AgendarServicio'
                            />
                        </ul>
                    </div>
                </div>
            </div>
        );

    }


