import React, {  Component, useState } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
import utils from '../Utils.js';
export default class CrearProductos extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            productoElegido: "",
            proveedores: [],
            nombre: "",
            precio: 0,
            stock: 0,
            proveedorId: 0,
            file: "",
            descripcion: "",
            mensajeError: "",
            mensajeSuccess: "",
            errors: {}
        }
       
    }


    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value})
    }

    manejadorImagen = e =>
    {
        this.setState({file: e.target.files[0]})
    }

/*     manejadorImagen = e =>
    {
        this.setState({imagen: e.target.files[0]})
    }
 */

    cargarDatos = () => {
        console.log("cargardatos");
        if(this.handleValidation()){  
            const datos = new FormData();
            console.log("form data");
            datos.append("nombre", this.state.nombre);
            datos.append("descripcion", this.state.descripcion);
            datos.append("precio", this.state.precio);
            datos.append("stock", this.state.stock);
            datos.append("idProveedor", this.state.proveedorId);
            datos.append("file", this.state.file);
            axios.post(url + '/producto/insertar', datos) 
                .then(response => {
                    console.log(response.status)
                    if (response.status === 200) {
                        this.setState({ 
                            mensaje: "ingreso correcto", 
                            mensajeSuccess: "ok",
                            proveedorId:0,
                            nombre:"",
                            precio:"",
                            stock:"",
                            descripcion:"",
                            file:""
                        });
                        //esto de abajo refresca la pagina muy rapido y no deja ver el mensaje de ingreso 
                        // document.getElementById("proveedor").value = "";
                        // document.getElementById("nombre").value = "";
                        // document.getElementById("precio").value = "";
                        // document.getElementById("stock").value = "";
                        // document.getElementById("descripcion").value = "";
                        // document.getElementById("file").value = "";
                      
                    } else {
                        this.setState({ mensaje: "ingreso incorrecto", mensajeError: "error" });
                    }
                }).catch(error => {
                    console.log(error);
                })
        }
    }

    elegirProveedor = event => {
        console.log(event.target.value)
        this.setState({
            proveedorId: event.target.value,
        });
    }

    componentDidMount = () => {
        axios.get(url + '/proveedor/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                proveedores: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    handleValidation(){
        console.log("handelvalidartio");
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Proveedor.
        if (this.state.proveedorId ===  null) {
            formIsValid = false;
            errors["proveedorId"] = "Debe ingresar el proveedor!!!";
            document.getElementById("proveedorId").focus();
        } 

        //Nombre
        if (this.state.nombre === "") {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre!!!";
            document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== ""){
            if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["nombre"] = "El nombre solo acepta letras!!!";
               document.getElementById("nombre").focus();
            }        
        }

        //Precio

        if (this.state.precio === "") {
            formIsValid = false;
            //errors["precio"] = utils.soloNumeros(this.state.precio);
         errors["precio"] = "Debe ingresar el precio!!!";
            document.getElementById("precio").focus();
         }

        //Precio
        if (this.state.precio <= 0) {
            formIsValid = false;
            errors["precio"] = "Ingrese números positivos!";
            document.getElementById("precio").focus();
        } 
        

        //Precio
        /* if (this.state.precio === 0) {
            formIsValid = false;
            errors["precio"] = "Debe ingresar el precio!";
            document.getElementById("precio").focus();
        }else if (! this.soloNumeros) {
            formIsValid = false;
            errors["precio"] = "Debe ingresar solo numeros!";
            document.getElementById("precio").focus();
        } */
        
        //Stock
        if (this.state.stock === 0) {
           formIsValid = false;
           errors["stock"] = "Debe ingresar el stock!!!";
           document.getElementById("stock").focus();
        }

        //Stock
        if (this.state.stock <= 0) {
            formIsValid = false;
            errors["stock"] = "Stock inválido!!!";
            document.getElementById("stock").focus();
        }
 
        //Descripción
         if (this.state.descripcion === "") {
            formIsValid = false;
            errors["descripcion"] = "Debe ingresar la descripción!!!";
            document.getElementById("descripcion").focus();
        }

        //Imagen
        if (this.state.file === "") {
            formIsValid = false;
            errors["imagen"] = "Debe ingresar una imagen!!!";
            document.getElementById("file").focus();
        }

        this.setState({errors: errors});
       return formIsValid;
   }


    render() {
        return (
            <div>
                <div>
                    <h1>Alta Producto</h1>
                     {/* <form method="post" encType="multipart/form-data">  */}
                    <form  encType="multipart/form-data">                         
                        <span style={{color: "red"}}>{this.state.errors["nombre"]}</span>
                        <br/>
                        <input type="text" id="nombre" class="fadeIn second"
                            name="nombre"  placeholder="Nombre del producto" onChange={this.manejadorOnChange} required/>
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["descripcion"]}</span>
                        <br/>
                        <textarea  id="descripcion" name="descripcion"  placeholder="Descripción del producto" cols="70" rows="4" onChange={this.manejadorOnChange}></textarea>
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["precio"]}</span>
                        <br/>
                        <input type="text"  min="0" id="precio" class="fadeIn second" name="precio" placeholder="Precio del producto" onChange={this.manejadorOnChange} />
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["stock"]}</span>
                        <br/>
                        <input type="text" min="0" id="stock" class="fadeIn second" name="stock" placeholder="Stock del producto" onChange={this.manejadorOnChange} />
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["proveedorId"]}</span>
                        <br/>
                        <select name="proveedor" id="proveedor" className="form-control" onChange={this.elegirProveedor}>
                            {this.state.proveedores.map(proveedorElegido => (
                                <option
                                    key={proveedorElegido}
                                    name="proveedor"
                                    value={proveedorElegido.id}
                                    selectedValue={proveedorElegido}
                                >{proveedorElegido.nombre}</option>
                            ))}
                        </select>
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["file"]}</span>
                        <br/>
                         <input type="file" id="file" class="fadeIn second" name="file"
                        accept="image/png, image/jpg, image/jpeg" placeholder="Imagen del producto" 
                        onChange={this.manejadorImagen} /> 
                        <br/><br/>
                        <button className="btn btn-primary" onClick={this.cargarDatos} type="submit">Enviar</button>
                        <br/><br/>

                        {/* <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton}>Crear</ReactBoostrap.Button>  */}
                    
                    </form>

                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                    
                </div>
            </div>
        )
   }
}
