import React, { componentDidMount, Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';


export default class ModificarUsuario extends Component {
    constructor(props) {
        super(props);
        this.state = {                    
            usuario:'',           
            nombre:'',         
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",            
        }
    }
    componentDidMount = () => {
        console.log(this.props.match.params.id)
        axios.get(url + '/usuarios/buscarPorId?id=' + this.props.match.params.id).then((resp) => {                    
            this.setState({                
                usuario:resp.data                              
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    botonModificar = () =>
    {     
        if (this.state.nombre !== "") {            
            axios.post(url + '/usuarios/modificar/?id=' + this.props.match.params.id + '&nombre=' + this.state.nombre +
            '&direccion='+ this.state.direccion +'&email=' + this.state.email +'&telefono=' + this.state.telefono)
            .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }
    
                }).catch(error =>
                {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
        }
    }


    render() {
        const {nombre,password} = this.state
        return (
            <div>
                <h1>Modificar Usuarios</h1>
                <form onSubmit={this.submit}>
                    <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + usuario.nombre} onChange={this.manejadorOnChange} />                    
                    <input type="text" name='password' value={direccion} placeholder={"direccion: " + usuario.direccion} onChange={this.manejadorOnChange} />                    
                    <br /><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
                </form>
                {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
            </div>
        )
    }
}
