import React,{Component} from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { url } from '../API.js';
import Button from 'react-bootstrap/Button'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
export default class ListarNotificacionesUsuario extends Component {

    constructor() {
      super();
      this.state = {
          notificaciones: [],
          mensaje: "",
          idCli:1,
      }
  }
  responderNotificacion= (serId) => {
    // console.log(serId);
    // axios.post(url + '/tareasagendadas/listasTareasServicio/?id=' + serId).then((resp) => {
    //   console.log(resp.data);
    //   this.setState({
    //     tareas: resp.data,
    //     estaCliqueado: true,
    //     idServicio: serId,
    //   });
    // }).catch((error) => {
    //   console.log(error);
    // }
    // )
  }
  componentDidMount = () => {
    axios.post(url + '/notificacion/verNotificacionesDeUsuario/?idCli='+ this.state.idCli).then((resp) => {
        console.log(resp.data);
        this.setState({
          notificaciones: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
};

render() {
    return (
      <div>
        <h1 >Listar Notificaciones</h1>
        <div>
          <ReactBoostrap.Table  className="table " hover variant="light-dark">
            <thead>
              <th scope="col">Id</th >
              <th scope="col">Servicio</th>
              <th scope="col">Estado del servicio</th>
              <th scope="col">Descripcion</th>
              </thead>              
                <tbody>
                  {       
                   this.state.notificaciones.map(elemento => (
                    <tr key={elemento.id} value={elemento} id={elemento.id}>
                      <td>{elemento.id}</td>
                      <td>{elemento.servicioAgendado.servicio.nombre}</td>
                      <td>{elemento.servicioAgendado.estado.nombre}</td>
                      <td>{elemento.descripcion}</td>
                      <td>
                        <ButtonGroup>
                          <Button className="button" onClick={this.responderNotificacion.bind(this, elemento.id)}>Responder</Button>
                        </ButtonGroup>
                      </td>
                    </tr>
    
                  ))
                  
                  }
                </tbody>
          </ReactBoostrap.Table>
        </div>
      </div>
    );

  }
}
