import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';

export default class CrearEstado extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",
            estadoElegido: "",
            nombre: ""
        }
       
    }

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    manejadorBoton = () =>
    {
        if (this.state.nombre !== "") {            
            axios.post(url + '/estado/insertar?nombre='+ this.state.nombre)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "Debe ingresar un nombre para el estado",mensajeError:"error" });
        }
    }

    render() {
        const {nombre} = this.state
        return (
            <div>
                <div>
                    <h1>Alta Estado</h1>
                    <form onSubmit={this.submit}>
                        <input type="text"  class="fadeIn second" name="nombre" placeholder="Nombre del estado" value={nombre} onChange={this.manejadorOnChange} required/>
                        <br/> <br/>
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }

}
