import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { url } from '../API.js';
import Calendar from 'react-calendar'
import 'react-calendar/dist/Calendar.css';


export default class AgendarServicio extends Component {

    constructor(props) {
        super(props);
        console.log(props.usa)
        this.state = {
            servicios: [{}],
            servicioElegido: "",
            mensaje: "",
            empleado:props.usa,
            empleadoid:1,
            idCli: 0,//a modo de prueba el id de cliente
            idSer: null,
            fechaElegida: new Date(),
            mensajeError: "",
            mensajeSuccess: "",
            horarios: [
                { hora: "9 am"},
                { hora: "10 am" },
                { hora: "11 am" },
                { hora: "12 am" },
                { hora: "1 pm"},
                { hora: "2 pm" },
                { hora: "3 pm" },
                { hora: "4 pm" },
                { hora: "5 pm" },
                { hora: "6 pm" },
            ],
            horarioElegido: "",
            horas:[],
            
        }
    }
    // agarrarDate = fecha => {
    //     this.setState({
    //         fechaElegida: fecha.target.value
    //     })
    // }
    agarrarDate = fecha => {

        this.setState({
            fechaElegida: fecha,//CON CALENDAR NO VA EL TARGET
        })
        this.traerHorasLibres();
        console.log(this.state.fechaElegida);
    }

    traerHorasLibres = () => {
        axios.post(url + '/servicioAgendado/horasLibres?idSer=' + this.state.idSer + "&fechaElegida="
         + this.state.fechaElegida )
        .then(response => {
            if (response.status === 200) {
                this.setState({
                     mensaje: "Ingreso Correcto", 
                     mensajeSuccess: "ok",
                     horas:response.data,
                    });
            }

        }).catch(error => {
            console.log(error);
            this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
        })

    }
    manejadorBoton = (e) => {
        e.preventDefault();
        console.log(this.state.idSer);
        //console.log(this.state.empleado.id);
        axios.post(url + '/servicioAgendado/insertar?idSer=' + this.state.idSer + "&idCli="
            + this.state.empleadoid + "&fechaElegida=" + this.state.fechaElegida +
            "&horarioElegido=" + this.state.horarioElegido)
            .then(response => {
                if (response.status === 200) {
                    this.setState({ mensaje: "Ingreso Correcto", mensajeSuccess: "ok" });
                }

            }).catch(error => {
                console.log(error);
                this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
            })
           
    }
    componentDidMount = () => {
        axios.get(url + '/servicio/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                servicios: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };
    handleChange = event => {
        console.log(event.target.value)
        this.setState({
            idSer: event.target.value,
        });
    }

    changeHorario = event => {
        console.log(event.target.value)
        this.setState({
            horarioElegido: event.target.value,
        });
    }
    render() {
        // var date = new Date();
        // var day = date.getDate();
        // var month = date.getMonth() + 1;
        // var year = date.getFullYear();
        // if (month < 10) month = "0" + month;
        // if (day < 10) day = "0" + day;
        // var today = year + "-" + month + "-" + day;

        var dateValue = new Date();
        // var startDate  = new Date(new Date().getFullYear(), new Date().getMonth,new Date().getDay());
        // var endDate  = new Date(new Date().getFullYear(), new Date().getMonth,new Date().getDay());
        return (
            
            <div >
                <h1  >Agende su servicio</h1>
                <div className="">
                    <select
                        onChange={this.handleChange}
                        className="form-control"
                        placeholderText="Servicios">
                        {/* <option hidden selected id="seleccionarServicio">Servicio</option> */}
                        {this.state.servicios.map(servicioElegido => (
                            <option
                                key={servicioElegido}
                                name="servicio"
                                value={servicioElegido.id}
                                selectedValue={servicioElegido}
                            >{servicioElegido.nombre}</option>
                        ))}</select>
                    {/* <select onChange={this.changeHorario}
                        className="form-control"
                        placeholderText="Horarios">
                        {this.state.horarios.map(horario => (
                            <option
                                key={horario}
                                name="horario"
                                value={horario.hora}
                                selectedValue={horario}
                            >{horario.hora}</option>
                        ))}
                    </select> */}

                    <form onSubmit={this.submit}>
                        {/* <input type="date" id="agendarServicio" class="fadeIn second" name="fechaElegida" placeholder="elija fecha"
                            value={this.state.fechaElegida} min={today} required onChange={this.agarrarDate} />*/}
                      <div className="fake-calendar">
                        <Calendar   className="calendar"
                          
                            placeholder="elija fecha"
                            required
                            activeStartDate ={dateValue}
                            
                            value={dateValue}
                            minDate={dateValue}
                            onChange={this.agarrarDate}
                           
                        />
                        </div>
                        <ReactBoostrap.Button type="submit" variant="success" className="fadeIn fourth" value="Crear Servicio"
                            onClick={this.manejadorBoton}>Crear</ReactBoostrap.Button >
                    </form>
                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        );

    }
}
