import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 
import '../App.css';

export default class CrearCategoria extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",           
            nombre: "",
            descuento: "",
            mensajeError: "",
            mensajeSuccess: "",
            errors: {}
        }
    }

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
           formIsValid = false;
           errors["nombre"] = "Debe ingresar el nombre!!!";
           document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== ""){
            if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["nombre"] = "El nombre solo acepta letras!!!";
               document.getElementById("nombre").focus();
            }        
        }
 
        //Descuento
        if (this.state.descuento === "") {
            formIsValid = false;
            errors["descuento"] = "Debe ingresar el descuento!!!";
            document.getElementById("descuento").focus();
        }
 
        this.setState({errors: errors});
        return formIsValid;
    }


    manejadorBoton = () =>
    {
        if(this.handleValidation()){             
            axios.post(url + '/categoria/insertar?nombre='+ this.state.nombre + 
            '&descuento='+ this.state.descuento)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                        document.getElementById("nombre").value = "";
                        this.state.nombre = "";
                        document.getElementById("descuento").value = "";
                        this.state.descuento = "";
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        }
    }

    render() {
        //const {nombre,descuento} = this.state
        return (
            <div>
                <div>
                    <br/>
                    <br/>                    
                    <h1>Alta Categoria</h1>
                    <form onSubmit={this.submit}>
                        <span style={{color: "red"}}>{this.state.errors["nombre"]}</span>
                        <br/>
                        <input type="text" id="nombre" class="fadeIn second"
                            name="nombre"  placeholder="Nombre de la categoría" onChange={this.manejadorOnChange} required/>
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["descuento"]}</span>
                        <br/>
                        <input type="text" id="descuento" class="fadeIn second"
                            name="descuento"  placeholder="Descuento de la categoría" onChange={this.manejadorOnChange} required/>
                        <br/><br/>
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }
}
