import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import { url } from './API.js';
import { Redirect } from "react-router-dom";
import nav from './Nav'
import App from '../App.js'

export default class Login extends Component {
  constructor() {
    super();
    this.state = {
     form:{ user: "",
      password: "",
    },
    usuario:{},
    logged:null,
    }
}
  manejadorOnChange = async e => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    })
    console.log(this.state.form);
  }


  manejadorBoton = (e) => {
    e.preventDefault();
    axios.get(url + '/usuario/buscar/?nombre='
      + this.state.form.user + '&password=' + this.state.form.password)
      .then(response => {
        this.setState({
          usuario: response.data,
          logged:true,
      });
        console.log(response);
      }).catch(error => {
        console.log(error);
      })
      console.log(this.state.usuario);
  }

  render() {
    console.log(this.state.usuario);

 if(this.state.logged){return <App  usuario={this.state.usuario} ok={true} />}
 else if(!this.state.logged){
     return (
   
      <div class="wrapper fadeInDown">
        <div id="formContent">
          <div class="fadeIn first">
            <img src="https://i.ibb.co/Q6FHzPJ/CAR-CLEAN.jpg" id="icon" width="100px" alt="User Icon" />
          </div>
          <br></br>
          <form onSubmit={this.onSubmit}>
            <input type="text" id="login" name="user" placeholder="usuario" onChange={this.manejadorOnChange} required/>
            <input type="password" id="password" name="password" placeholder="password" onChange={this.manejadorOnChange} required/>
            <input type="submit" value="Log In" onClick={this.manejadorBoton}   />
          </form>
          <div id="formFooter">
            <a class="underlineHover" href="/CrearUsuario">Crear usuario</a>
          </div>
        </div>
      </div>
    );
     }
  }

}
