import React,{Component} from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { url } from '../API.js';


export default class ListarProductos extends Component {

  constructor() {
    super();
    this.state = {
        productos: [],
        searchTerm: "",
      }
  }

  componentDidMount = () => {
    axios.get(url + '/producto/listarActivos').then((resp) => {
        console.log(resp.data);
        this.setState({
            productos: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };

  manejadorOnChange = e =>{
    this.setState({
      searchTerm:e.target.value,
    })
  }

  render() {
      return(
        <div className="App">
          <h1>Listado Productos</h1>
          <input type="text" 
            placeholder="Buscar por nombre" 
            onChange={this.manejadorOnChange}
          />
          <ReactBoostrap.Table  hover variant="light-dark">
                <table className="table">
                  <thead>
                    <th scope="col">Foto</th >
                    <th scope="col">Nombre</th>
                    <th scope="col">Precio</th>
                    <th scope="col">Cantidad de stock</th>
                    <th scope="col">Proveedor</th>
                  </thead>
                  <tbody>
                  {
                    this.state.productos.filter((elemento) => {
                      if (this.searchTerm === "") {
                        return elemento
                      } else if (elemento.nombre.toLowerCase().includes(this.state.searchTerm.toLowerCase())) {
                        return elemento
                      }
                    }).map(elemento => (
                        <tr key={elemento.id} value={elemento} id={elemento.id}>
                          <td><img src={'/images/'+ elemento.imagen}  width="100" height="75"></img></td>
                          <td>{elemento.nombre}</td>
                          <td>{elemento.precio}</td>
                          <td>{elemento.stock}</td>
                          <td>{elemento.proveedor.nombre}</td> 
                        </tr>
                    ))
                  }
                  </tbody>
              </table>
              </ReactBoostrap.Table>
        </div>
    )

}



}
