import React, { componentDidMount,Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 
import { Link } from 'react-router-dom';


export default class BorrarProducto extends Component
{
  constructor()
  {
    super();
    this.state = {
    productos: [],
        ProductoBorrado: "",
        ProductoIdBorrado: 0,
        mensaje:"",   
        data:''     
    }
  }
  componentDidMount = () => {
    axios.get(url + '/productos/listar').then((resp) => {
        this.setState({
        productos: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };
  elegirProductoBorrado = productoBorrado =>
  {
    this.setState({ProductoIdBorrado: productoBorrado.target.value
    })
    if (this.state.ProductoIdBorrado !== 0) {
      this.borrarBoton();
      
    }
    console.log(this.state.ProductoIdBorrado);
  }
  borrarBoton = () =>
  {
    axios.post('http://localhost:9090/productos/borrar?id=' + this.state.ProductoIdBorrado)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
          this.componentDidMount();
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
  }
  
  render()
  { 
      return (      
      <div>      
          <h1>Borrar Productos</h1>
            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th>
                <th scope="col"></th>
              </thead>
              <tbody>
              {
                this.state.productos.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.elegirProductoBorrado}>borrar</ReactBoostrap.Button></td>                     
                   <td>
                  <Link className='btn btn-primary'  to={'/ModificarProducto/' + elemento.id} >Modificar</Link>
                  </td> 
                    
                  </tr>
                ))
                }
                  </tbody>                 
              </table>                   
          </ReactBoostrap.Table>          
      </div>
    )
  }
}

