import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import { url } from './API.js';
import { Redirect } from "react-router-dom";
import nav from './Nav'
import App from '../App.js'

export default class Login extends Component {
  constructor() {
    super();
    this.state = {
     form:{ user: "",
      password: "",
    },
    usuario:{},
    logged:null,   
    }
}
  manejadorOnChange = async e => {      
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    })   
  }


  manejadorBoton = (e) => {    
    e.preventDefault();
    axios.get(url + '/usuario/buscarPorEmail/?email='
      + this.state.form.user + '&password=' + this.state.form.password)
      .then(response => {
        this.setState({
          usuario: response.data,          
          logged:true,
      });   
      console.log(this.state.usuario);       
      console.log(this.state.logged);   
      }).catch(error => {
        console.log(error);
      })      
  }

  render() {   
    if(this.state.logged == true && this.state.usuario.perfil.nombre == "empleado"){
        alert("Soy empleado!")
    }else if(this.state.logged == true && this.state.usuario.perfil.nombre == "cliente"){
      alert("Soy cliente!")
    }else if(this.state.logged == true && this.state.usuario.perfil.nombre == "admin"){
      alert("Soy admin!")
    }
     else
     return (   
      <div class="wrapper fadeInDown">
        <div id="formContent">
          <div class="fadeIn first">
            <img src="https://i.ibb.co/Q6FHzPJ/CAR-CLEAN.jpg" id="icon" width="100px" alt="User Icon" />
          </div>
          <br></br>
          <form onSubmit={this.onSubmit}>
            <input type="text" id="login" name="user" placeholder="Email del usuario" onChange={this.manejadorOnChange} required/>
            <input type="password" id="password" name="password" placeholder="Password" onChange={this.manejadorOnChange} required/>
            <input type="submit" value="Log In" onClick={this.manejadorBoton}   />
          </form>
          <div id="formFooter">
            <a class="btn btn primary" href="/RegistroUsuario">Registrarse</a>
          </div>
        </div>
      </div>
    );
  } 




}
