import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
export default class CrearProductos extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            productoElegido: "",
            mensajeError: "",
            mensajeSuccess: "",
            proveedores: [],
            nombre: "",
            codigoProveedor: 0,
            precio: 0,
            stock: 0,
            proveedorId: 0,
            errors: {}
        }
    }

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    elegirProveedor = event => {
        console.log(event.target.value)
        this.setState({
            proveedorId: event.target.value,
        });
    }

    componentDidMount = () => {
        axios.get(url + '/proveedor/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                proveedores: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Proveedor.
        if (this.state.proveedorId === 0) {
            formIsValid = false;
               errors["proveedorId"] = "Debe ingresar el proveedor!!!";
        } 

        //Nombre
        if (this.state.nombre === "") {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre!!!";
        }

        if (this.state.nombre !== ""){
            if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["nombre"] = "El nombre solo acepta letras!!!";
            }        
        }
    

        //Precio
        if (this.state.precio === 0) {
            formIsValid = false;
            errors["precio"] = "Debe ingresar el precio!!!";
        }

        //Stock
        if (this.state.stock === 0) {
           formIsValid = false;
           errors["stock"] = "Debe ingresar el stock!!!";
        }
       this.setState({errors: errors});
       return formIsValid;
   }


    manejadorBoton = () => {
        if(this.handleValidation()){
            axios.post(url + '/producto/insertar?nombre=' + this.state.nombre + "&precio=" + this.state.precio +
                "&stock=" + this.state.stock + "&proveedor=" + this.state.proveedorId)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({ mensaje: "ingreso correcto", mensajeSuccess: "ok" });
                        document.getElementById("proveedor").value = "";
                        document.getElementById("nombre").value = "";                        
                        document.getElementById("precio").value = "";
                        document.getElementById("stock").value = "";
                        this.state.proveedorId = 0;
                        this.state.nombre = "";
                        this.state.precio = 0;
                        this.state.stock = 0;
                        this.state.proveedorId = 0;
                    } else {
                        this.setState({ mensaje: "ingreso incorrecto", mensajeError: "error" });
                    }
                }).catch(error => {
                    console.log(error);
                })
            }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Alta Producto</h1>
                    <span style={{color: "red"}}>{this.state.errors["proveedorId"]}</span>
                    <br/>
                    <select name="proveedor" id="proveedor" className="form-control" onChange={this.elegirProveedor}>
                        {this.state.proveedores.map(proveedorElegido => (
                            <option
                                key={proveedorElegido}
                                name="proveedor"
                                value={proveedorElegido.id}
                                selectedValue={proveedorElegido}
                            >{proveedorElegido.nombre}</option>
                        ))}
                    </select>
                    <br/>
                    <form onSubmit={this.submit}>
                        <span style={{color: "red"}}>{this.state.errors["nombre"]}</span>
                        <br/>
                        <input type="text" id="nombre" class="fadeIn second"
                            name="nombre"  placeholder="Nombre del producto" onChange={this.manejadorOnChange} required/>
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["codigoProveedor"]}</span>        
                        <br/>
                        <input type="number" id="precio" class="fadeIn second" name="precio" placeholder="Precio del producto" onChange={this.manejadorOnChange} />
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["stock"]}</span>
                        <br/>
                        <input type="number" id="stock" class="fadeIn second" name="stock" placeholder="Stock del producto" onChange={this.manejadorOnChange} />
                        <br/><ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
                    </form>

                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )
   }
}
