import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { connect } from 'react-redux'


export default class ListarClientes extends Component {

  constructor() {
    super();
    this.state = {
      clientes: [],
      mensaje: "",
      nombre: "",
      direccion: "",
      categoria: "",     
    }
  }

  componentDidMount = () => {
    axios.get('http://localhost:9090/clientes/listar').then((resp) => {
      console.log(resp.data);
      this.setState({
        clientes: resp.data
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };
  render() {
    return (
      <div>
        <h1 >Listar Clientes</h1>
        <div>
          <ReactBoostrap.Table hover variant="light-dark">
            <table className="table ">
              <thead>
                <td scope="col">ID</td >
                <th scope="col">Nombre</th>
                <th scope="col">Direccion</th>
                <th scope="col">Categoria</th>              
              </thead>
              <tbody>
                {
                  this.state.clientes.map(elemento => (
                    <tr key={elemento.id} value={elemento} id={elemento.id}>
                      <td>{elemento.id}</td>
                      <td>{elemento.nombre}</td>
                      <td>{elemento.direccion}</td>
                      <td>{elemento.categoria}</td>                     
                    </tr>
                  ))
                }
              </tbody>
            </table>
          </ReactBoostrap.Table>
        </div>
      </div>
    );

  }
}
