import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import{ url} from '../API.js'
export default class BorrarTarea extends Component
{
  constructor()
  {
    super();
    this.state = {
      tareas: [],
      tareaBorradoId: 0,
    }
  }
  componentDidMount = () => {
    axios.get(url + '/tarea/listarActivos').then((resp) => {
        this.setState({
            tareas: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };
 
  borrarBoton = (id) =>
  {
    this.setState({
      tareaBorradoId: id
    })
    axios.post(url + '/tarea/borrar?id=' +id)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
          this.componentDidMount();
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
  }
  
  render()
  {

    return (
      <div>
       
          <h1>Borrar Tareas</h1>
         

            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th>
                <th scope="col"></th>
              </thead>
              <tbody>
              {
                this.state.tareas.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={() => this.borrarBoton(elemento.id)}>borrar</ReactBoostrap.Button></td>
                  </tr>
                ))
                }
                  </tbody>
              </table>
          </ReactBoostrap.Table>
          
        
      </div>
    )
  }
}
