import React, { componentDidMount, Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default class BorrarEmpleado extends Component {
  constructor() {
    super();
    this.state = {
      empleados: [],
      empleadoBorradoId: 0,
    }
  }
  componentDidMount = () => {
    axios.get('http://localhost:9090/empleados/listar').then((resp) => {
      this.setState({
        empleados: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };
  elegirEmpleadoBorrado = empBorrado => {
    this.setState({
      empleadoBorradoId: empBorrado.target.value
    })
    if (this.state.empleadoBorradoId !== 0) {
      this.borrarBoton();

    }
    console.log(this.state.empleadoBorradoId);
  }
  borrarBoton = () => {
    axios.post('http://localhost:9090/empleados/borrar?id=' + this.state.empleadoBorradoId)
      .then(response => {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
          this.componentDidMount();
        }

      }).catch(error => {
        console.log(error);
      })
  }

  render() {

    return (
      <div>

        <h1>Borrar empleados</h1>


        <ReactBoostrap.Table hover variant="light-dark">
          <table className="table ">
            <thead>
              <th scope="col">ID</th >
              <th scope="col">Nombre</th>
              <th scope="col"></th>
            </thead>
            <tbody>
              {
                this.state.empleados.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.elegirUsuarioBorrado}>borrar</ReactBoostrap.Button></td>
                    <td>
                      <Link className='btn btn-primary' to={'/ModificarEmpleado/' + elemento.id} >Modificar</Link>
                    </td>
                  </tr>
                ))
              }
            </tbody>
          </table>
        </ReactBoostrap.Table>


      </div>
    )
  }
}
