import React, { Component } from 'react';
import axios from 'axios';
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 
import { Link } from 'react-router-dom'


export default class ModificarEstado extends Component
{
  constructor(props)
  {
    super(this.props);
    this.state = {
    estado: "",
        EstadoModificado: "",
        EstadoIdModificado: 0,
        mensaje:"",
    }
  }
  
  componentDidMount = () => {
    axios.get(url + '/estado/buscarPorId' + this.props.match.params.id).then((resp) => {
        this.setState({
        estado: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };
  
  elegirEstadoModificado = EstadoModificado =>
  {
    this.setState({EstadoIdModificado: EstadoModificado.target.value
    })
    if (this.state.EstadoIdModificado !== 0) {
      this.borrarBoton();
      
    }
    console.log(this.state.EstadoIdModificado);
  }

  borrarBoton = () =>
  {
    axios.post(url +'/estado/modificar?id=' + this.state.EstadoIdModificado)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "estado modificado" });
          this.componentDidMount();
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
  }
  
  render()
  {

    return (
      <div>
      <h1>Modificar Estado</h1>
      <form onSubmit={this.submit}>
          <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + estado.nombre} onChange={this.manejadorOnChange} />
          <input type="text" name='valor' value={valor} placeholder={"Fecha de Ingreso: " + estado.fechaIngreso} onChange={this.manejadorOnChange} />
                            
          <br /><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
      </form>
      {this.state.mensajeSuccess==="ok"?
              <div class="alert alert-success" role="alert">
                  { this.state.mensaje }
                  </div> : (this.state.mensajeError === "error")?
                  <div class="alert alert-danger" role="alert">
                  { this.state.mensaje }
                  </div>:null
              }
      </div>
    );
  }
}
