import React, { componentDidMount,Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 

export default class ModificarProducto extends Component
{
  constructor()
  {
    super();
    this.state = {
    productos: [],
        ProductoModificado: "",
        ProductoIdMod: 0,
        mensaje:"",
    }
  }
  componentDidMount = () => {
    axios.get(url + '/productos/listar').then((resp) => {
        this.setState({
        productos: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };

  elegirProductoModificado = ProductoModificado =>
  {
    this.setState({ProductoIdMod: ProductoModificado.target.value
    })
    if (this.state.ProductoIdMod !== 0) {
      this.botonModificar();
      
    }
    console.log(this.state.ProductoIdMod);
  }

  botonModificar = () =>
  {
    axios.post('http://localhost:9090/productos/modificar?id=' + this.state.ProductoIdMod)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "modificado correctamente" });
          this.componentDidMount();
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
  }
  
  render()
  {

    return (
      <div>
       
          <h1>Modificar Producto</h1>
            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th>
                <th scope="col"></th>
              </thead>
              <tbody>
              {
                this.state.productos.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-primary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.elegirProductoModificado}>Modificar</ReactBoostrap.Button></td>
                  </tr>
                ))
                }
                  </tbody>
              </table>
          </ReactBoostrap.Table>
          
        
      </div>
    )
  }
}
