import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { url } from '../API.js';
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';



export default class AgendarServicio extends Component {

    constructor(props) {
        super(props);
        this.state = {
            diaElegido: '',
            servicios: [],
            servicioElegido: "",
            mensaje: "",
            idCli: 0,
            serviciosFull: [],
            serviciosMedios: [],
            serviciosBasicos: [],
            selectedDay: "",
            serviciosAgendados: [],
            fechasConvertidas: {},
            idSer: (-1),
            mensajeError: "",
            mensajeSuccess: "",
            horas: [],
            horarios: [
                { hora: "9 am" },
                { hora: "10 am" },
                { hora: "11 am" },
                { hora: "12 am" },
                { hora: "2 pm" },
                { hora: "3 pm" },
                { hora: "4 pm" },
                { hora: "5 pm" },
                { hora: "6 pm" },
            ],
            horarioElegido: "",
            servicioDia:[],
        }
    }


    manejadorBoton = (e) => {
        e.preventDefault();
        console.log(this.state.idSer)
        console.log(localStorage.getItem('usuario') + 22)
        console.log(this.state.selectedDay.toLocaleDateString())
        console.log(this.state.horarioElegido)
        if (this.state.idSer == -1 || this.state.selectedDay == '' || this.state.horarioElegido == '') {
            this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
        } else {
            this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
            axios.post(url + '/servicioAgendado/insertar?idSer=' + this.state.idSer + "&idUsu=" + localStorage.getItem('usuario')
                + "&fechaElegida=" + this.state.selectedDay.toLocaleDateString() + "&horarioElegido=" + this.state.horarioElegido)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({
                             mensaje: "Ingreso Correcto",
                              mensajeSuccess: "ok" });
                    }

                }).catch(error => {
                    console.log(error);
                    this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
                })
        }
    }

    componentDidMount = () => {
        axios.get(url + '/servicio/listarActivos').then((resp) => {
            this.setState({
                servicios: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
        axios.get(url + '/servicioAgendado/listarActivos').then((resp) => {
            this.setState({
                serviciosAgendados: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
        axios.get(url + '/servicioAgendado/listarAgenda?num=3').then((resp) => {
            this.setState({
                serviciosFull: resp.data,
            });
            this.cargarFecha()
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    cargarFecha = () => {
        const today = new Date();
        const fechasAntes = [{ before: today }];
        const fechas = fechasAntes;
        this.state.serviciosFull.forEach(e => {
            fechas.push(new Date(e))
        });
        this.setState({
            fechasConvertidas: fechas,
        })
        console.log(this.state.fechasConvertidas)
    }

    handleChange = event => {
        console.log(event.target.value)
        if (event.target.value == 3) {
            this.setState({
                horas: [{ hora: '9 am' }]
            })
        } else {
            this.setState({
                horas: this.state.horarios
            })
        }
        this.setState({
            idSer: event.target.value,
        });
        console.log(this.state.horas)
    }

    changeHorario = event => {
        console.log(event.target.value)
        this.setState({
            horarioElegido: event.target.value,
        });
    }

    handleDayClick = (day, { selected, disabled }) => {
        if (disabled) {
            return;
        }
        if (selected) {
            this.setState({ selectedDay: undefined });
            return;
        } else{
            this.setState({
                selectedDay: day,
               
        }); 
        this.llamarDia();     
        }
            
    }

    llamarDia = () =>{
        axios.get(url + '/servicioAgendado/llamarServicioDia?dia=' + this.state.selectedDay).then((resp) => {
            this.setState({
                servicioDia:resp.data,
            });
            this.cargarFecha()
        }).catch((error) => {
            console.log(error);
        }
        )
        console.log(this.state.servicioDia)
    }


    render() {
        //var holydays = [new Date('08/22/2021'), new Date('08/25/2021'), new Date('08/29/2021')];       
        return (
            <div>
                <h1>Agende su servicio</h1>
                <div className="container">
                    <label id="labeles" className="lead" >Agende su servicio</label>
                    <div className="">
                        <select
                            onChange={this.handleChange}
                            className="form-control"
                            placeholderText="Servicios">
                            <option disabled selected >Eliga un servicio</option>
                            {this.state.servicios.map(servicioElegido => (
                                <option
                                    key={servicioElegido}
                                    name="servicio"
                                    value={servicioElegido.id}
                                    selectedValue={servicioElegido}
                                >{servicioElegido.nombre}</option>
                            ))}</select>

                        <br />
                        <form onSubmit={this.submit}>
                            <br />
                            <div>
                                <DayPicker
                                    onDayClick={this.handleDayClick}
                                    selectedDays={this.state.selectedDay}
                                    disabledDays={this.state.fechasConvertidas}
                                />
                                {this.state.selectedDay ? (
                                    <p>{this.state.selectedDay.toLocaleDateString()}</p>
                                ) : (
                                    <p>Elija un d√≠a.</p>
                                )}
                            </div>
                            <br />
                            <label id="labeles" className="lead" >Ingrese su horario</label>
                            <select onChange={this.changeHorario}
                                className="form-control"
                                placeholderText="Horarios">
                                <option disabled selected >Eliga un horario</option>
                                {this.state.horas.map(horario => (
                                    <option
                                        key={horario}
                                        name="horario"
                                        value={horario.hora}
                                        selectedValue={horario}
                                    >{horario.hora}</option>
                                ))}
                            </select>
                            <br />
                            <ReactBoostrap.Button type="submit" variant="success" className="fadeIn fourth" value="Crear Servicio"
                                onClick={this.manejadorBoton}>Agendar Servicio</ReactBoostrap.Button >
                        </form>

                        {this.state.mensajeSuccess === "ok" ?
                            <div class="alert alert-success" role="alert">
                                {this.state.mensaje}
                            </div> : (this.state.mensajeError === "error") ?
                                <div class="alert alert-danger" role="alert">
                                    {this.state.mensaje}
                                </div> : null
                        }
                    </div>
                </div>
            </div>
        );
    }
}
