import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from "axios";


export default class ListarServicios extends Component {

  state = {
    data: []
  }
  componentDidMount() {
    this.peticionGet();
  }

  peticionGet = () => {
    axios.get('http://localhost:9090/servicios/listar').then(response => {
      this.setState({ data: response.data });
      console.log(response.data);
    }).catch(error => {
      console.log(error.message);
    })
  }


  render() {
    return (
      <div>
        <h1>ListarServicios</h1>
        <div>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Seleccionar</th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.data.map(servicio=>{
                return(
                  <tr>
                    <td>{servicio.id}</td>
                    <td>{servicio.nombre}</td>
                    <td>
                      <button className="btn btn-primary" onClick={()=>{this.seleccionarServicio(servicio); this.modalInsertar()}}></button>
                      {"   "}
                      <button className="btn btn-danger"  onClick={()=>{this.seleccionarServicio(servicio); this.setState({modalEliminar: true})}}></button>
                    </td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
        </div>
      </div>
    );
  }
}
