import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';

import { url } from '../API.js';
//import nav from '../Nav.js';

export default class CrearServicio extends Component {

    constructor() {
        super();
        this.state = {
            servicios: [],
            garantias: [],
            tareas: [],
            nombre: "",
            precio: 0,
            garantia: 0,
            horastrabajo: 0,
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",
            errors: {}
        }
    }

    componentDidMount = () => {
        axios.get(url + '/tarea/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                tareas: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
        axios.get(url + '/garantia/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                garantias: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    manejadorOnChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    handleValidation() {
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre!!!";
            document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== "") {
            if (!(this.state.nombre).match(/^[a-zA-Z]+$/)) {
                formIsValid = false;
                errors["nombre"] = "El nombre solo acepta letras!!!";
                document.getElementById("nombre").focus();
            }
        }

        //Precio
        if (this.state.precio === 0) {
            formIsValid = false;
            errors["precio"] = "Debe ingresar el precio!!!";
            document.getElementById("precio").focus();
        }

        //Garantia
        if (this.state.garantia === 0) {
            formIsValid = false;
            errors["garantia"] = "Debe ingresar la garant√≠a!!!";
            document.getElementById("garantia").focus();
        }

        //Horas de trabajo
        if (this.state.horastrabajo === 0) {
            formIsValid = false;
            errors["horastrabajo"] = "Debe ingresar la cantidad de horas  de trabajo!!!";
            document.getElementById("horastrabajo").focus();
        }

        this.setState({ errors: errors });
        return formIsValid;
    }

    elegirGarantia = event => {
        console.log(event.target.value)
        this.setState({
            garantia: event.target.value,
        });
    }

    manejadorBoton = () => {
        if (this.handleValidation()) {
            axios.post(url + '/servicio/insertar?nombre=' + this.state.nombre + "&precio="
                + this.state.precio + "&horasTrabajo=" + this.state.horastrabajo, '&GarantiaId=' + this.state.garantia)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({
                            mensaje: "Ingreso Correcto",
                            mensajeSuccess: "ok",
                            nombre: "",
                            precio: "",
                            horasTrabajo: ""
                        });
                        document.getElementById("nombre").value = "";
                        document.getElementById("precio").value = "";
                        document.getElementById("horasTrabajo").value = "";
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
                    }

                }).catch(error => {
                    console.log(error);
                })
        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Alta servicio</h1>
                    <form onSubmit={this.submit}>
                        <span style={{ color: "red" }}>{this.state.errors["nombre"]}</span>
                        <br />
                        <input type="text" id="nombre" className="fadeIn second" required
                            name="nombre" placeholder="Nombre del servicio" onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["precio"]}</span>
                        <br />
                        <ReactBoostrap.InputGroup   placeholder="Precio del servicio" className="mb-3">
                            <ReactBoostrap.InputGroup.Text>$</ReactBoostrap.InputGroup.Text>
                            <ReactBoostrap.InputGroup.Text type="number" id="precio" className="mb-3" required
                            name="precio" placeholder="Precio del servicio" onChange={this.manejadorOnChange}></ReactBoostrap.InputGroup.Text>
                            <ReactBoostrap.FormControl aria-label="Dollar amount (with dot and two decimal places)" />
                        </ReactBoostrap.InputGroup>

                        <input type="number" id="precio" className="mb-3" required
                            name="precio" placeholder="Precio del servicio" onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["horastrabajo"]}</span>
                        <br />
                        <input type="number" id="horastrabajo" className="fadeIn second" required
                            name="horastrabajo" placeholder="Horas de trabajo del servicio" onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["garantia"]}</span>
                        <br />
                        <label>Seleccione los meses de garantia</label>
                        <select name="garantia" id="garantia" className="form-control" onChange={this.elegirGarantia}>
                            <option disabled selected>Selecciona una opci√≥n</option>
                            {this.state.garantias.map(garantiaElegida => (
                                <option
                                    key={garantiaElegida}
                                    name="garantia"
                                    value={garantiaElegida.id}
                                    selectedValue={garantiaElegida}
                                >{garantiaElegida.cantidadMeses}</option>
                            ))}
                        </select>
                        <br />
                        <br />
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
                    </form>
                    <div>
                        <ReactBoostrap.Table className="table" hover variant="light-dark">
                            <thead>
                                <th scope="col">ID</th >
                                <th scope="col">Nombre</th>
                                <th scope="col">Descripcion</th>
                            </thead>
                            <tbody>
                                {
                                    this.state.tareas.map(elemento => (
                                        <tr key={elemento.id} value={elemento} id={elemento.id}>
                                            <td>{elemento.id}</td>
                                            <td>{elemento.nombre}</td>
                                            <td>{elemento.descripcion}</td>
                                        </tr>
                                    ))
                                }
                            </tbody>
                        </ReactBoostrap.Table>
                    </div>
                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )

    }
}
