import React, { componentDidMount, Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';


export default class CrearProductos extends Component {

    constructor(props){
        super(props);
 
        this.state = {
            mensaje: "",
            mensajeError: "",
            mensajeSuccess:"",
            nombre: "",
            precio: 0,
            stock: 0,
            fields: {},
            errors: {},
            proveedores:[],
            proveedorId: 0
        }
     }

     elegirProveedor = event => {
        console.log(event.target.value)
        this.setState({
            proveedorId: event.target.value,
        });
    }

    nombreOnChange = e => {
        this.setState({
            nombre: e.target.value,
            mensaje: "",
        })
    }

    precioOnChange = e => {
        this.setState({
            precio: e.target.value,
        })
    }

    stockOnChange = e => {
        this.setState({
            stock: e.target.value,
        })
    }

    proveedorOnChange = e => {
        this.setState({
            proveedorId: e.target.value,
        })
    }


    handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

         //Nombre
         if (this.state.nombre === "") {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre!!!";
         }

         if (this.state.nombre !== ""){
            if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["nombre"] = "El nombre solo acepta letras!!!";
            }        
        }

        //Precio
        if (this.state.precio === 0) {
            formIsValid = false;
            errors["precio"] = "Debe ingresar el precio!!!";
        }

        //Stock
        if (this.state.stock === 0) {
           formIsValid = false;
           errors["stock"] = "Debe ingresar el stock!!!";
        }

       //Proveedor.
        if (this.state.proveedorId === 0) {
        formIsValid = false;
           errors["proveedorId"] = "Debe ingresar el proveedor!!!";
       } 

       this.setState({errors: errors});
       return formIsValid;
   }

   peticionGet=()=>{
    axios.get('http://localhost:9090/proveedores/listar').then(response=>{
      this.setState({proveedores: response.data});
      console.log(response.data);
    }).catch(error=>{
      console.log(error.message);
    })
   }

    componentDidMount = () => {
        this.peticionGet();
    };

    handleChange(field, e){         
        let fields = this.state.fields;
        fields[field] = e.target.value;        
        this.setState({fields});
    }

    manejadorBoton = () => {
        if(this.handleValidation()){
            axios.post(url + '/productos/insertar?nombre=' + this.state.nombre + "&precio=" 
                + this.state.precio + "&proveedor=" + this.state.proveedorId)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto", mensajeSuccess: "ok" });
                        document.getElementById("crearProducto").value = "";
                        document.getElementById("crearProducto1").value = "";
                        this.state.nombre = "";
                        this.state.precio = 0;
                        this.state.proveedorId = 0;
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
                    }
                }).catch(error => {
                    console.log(error);
                })
        } 
    };



    render(){
        return (
             <div>     
                 <h1>Alta Producto</h1>      
                 <form onSubmit={this.submit}>
                        <span style={{color: "red"}}>{this.state.errors["nombre"]}</span>
                        <br/>
                        <input type="text" id="crearProducto" class="fadeIn second"
                            name="nombre" placeholder="Nombre del producto" onChange={this.nombreOnChange} />
                        <br/>    
                        <span style={{color: "red"}}>{this.state.errors["precio"]}</span>
                        <br/>
                        <input type="number" id="crearProducto1" class="fadeIn second"
                            name="precio" placeholder="Precio del producto" onChange={this.precioOnChange} />
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["stock"]}</span>
                        <br/>
                        <input type="number" id="crearProducto2" class="fadeIn second"
                            name="stock" placeholder="Stock del producto" onChange={this.stockOnChange} />
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["proveedorId"]}</span>
                        <select name="proveedor" id="select" className="form-control" style={{width:170}} 
                          onChange={this.elegirProveedor}>
                        {this.state.proveedores.map(proveedor => (
                            <option
                                key={proveedor.id}
                                name="proveedor"
                                value={proveedor.id}
                            >{proveedor.id}</option>
                        ))}</select>
                        <br/><br/>
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
               </form>
               {this.state.mensajeSuccess==="ok"?
                    <div class="alert alert-success" role="alert">
                        { this.state.mensaje }
                    </div> : (this.state.mensajeError === "error")?
                    <div class="alert alert-danger" role="alert">
                        { this.state.mensaje }
                    </div>:null
                }
             </div>
        )
    }


}
