import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
export default class CrearProveedor extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            nombre: "",
            direccion: "",
            email: "",
            telefono: "",
            mensajeError: "",
            fields: {},
            mensajeSuccess: "",
        }
    }
    manejadorOnChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    handleValidation() {
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (!fields["nombre"]) {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre!!!";
        }

        if (typeof fields["nombre"] !== "undefined") {
            if (!fields["nombre"].match(/^[a-zA-Z]+$/)) {
                formIsValid = false;
                errors["nombre"] = "El nombre solo acepta letras!!!";
            }
        }

        //Dirección
        if (!fields["direccion"]) {
            formIsValid = false;
            errors["direccion"] = "Debe ingresar la dirección!!!";
        }

        //Email
        if (!fields["email"]) {
            formIsValid = false;
            errors["email"] = "Debe ingresar el mail!!!";
        }

        if (typeof fields["email"] !== "undefined") {
            let lastAtPos = fields["email"].lastIndexOf('@');
            let lastDotPos = fields["email"].lastIndexOf('.');

            if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') === -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
                formIsValid = false;
                errors["email"] = "El mail no es válido!!!";
            }
        }

        //Teléfono.
        if (!fields["telefono"]) {
            formIsValid = false;
            errors["telefono"] = "Debe ingresar el teléfono!!!";
        }

        this.setState({ errors: errors });
        return formIsValid;
    }

    manejadorBoton = (e) => {
        e.preventDefault();
        if (this.handleValidation()) {
            let fields = this.state.fields;

            axios.post(url + '/proveedor/insertar?nombre=' + this.state.nombre +
                '&direccion=' + this.state.direccion + '&telefono=' + this.state.telefono + '&email=' + this.state.email)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto", mensajeSuccess: "ok" });
                    } else if (response.status === 404) {
                        this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
                    }

                }).catch(error => {
                    console.log(error);
                    this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" })
                    // this.setState({ mensaje: error.status })

                })
        }
    }

    render() {
        const { nombre, direccion, email, telefono } = this.state
        return (
            <div>
                <div>
                    <h1>Alta Proveedor</h1>
                    <form onSubmit={this.submit}>
                        <span style={{ color: "red" }}>{this.state.errors["nombre"]}</span>
                        <br />
                        <input type="text" class="fadeIn second" name="nombre" placeholder="Nombre del proveedor" value={nombre} onChange={this.manejadorOnChange} required />
                        <br/>
                        <span style={{color: "red"}}>{this.state.errors["direccion"]}</span>        
                        <br/>
                        <input type="text" class="fadeIn second" name="direccion" placeholder="Direccion del proveedor" value={direccion} onChange={this.manejadorOnChange} required />
                        <input type="text" class="fadeIn second" name="email" placeholder="Email del proveedor" value={email} onChange={this.manejadorOnChange} required />
                        <input type="text" class="fadeIn second" name="telefono" placeholder="Telefono del proveedor" value={telefono} onChange={this.manejadorOnChange} required />
                        <br /> <br />
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
                    </form>
                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )

    }
}
