import React,{Component} from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { connect } from 'react-redux'


export default class ListarProductos extends Component {

  constructor() {
    super();
    this.state = {
        productos: [],
    }
}

  componentDidMount = () => {
    axios.get('http://localhost:9090/productos/listar').then((resp) => {
        console.log(resp.data);
        this.setState({
            productos: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
};
  render() {
    return (
      <div>
        <h1 >Listar Productos</h1>
        <div>
          <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th><th scope="col">Precio</th>
             <th scope="col">CÃ³digo Proveedor</th><th scope="col">Stock id</th>
              </thead>
              <tbody>
              {
                this.state.productos.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td>{elemento.precio}</td>
                    {/* <td>{elemento.codigoProveedor}</td>
                    <td>{elemento.stock.id}</td> 
                    LES SAQUE ESTO POR QUE SE CAE AL NO TENER UN STOCK ID SE CAE 
                    HAY QUE TOMAR ESE ID Y PONER EL NOMBRE DEL PROVEEDOR AHI LLAMANDO A LA API Y
                    MANDANDO EL ID DE ESE PROVEEDOR
                    */} 
                  </tr>
                ))
                }
                  </tbody>
              </table>
          </ReactBoostrap.Table>
        </div>
      </div>
    );

  }
}
